INSERT INTO `tb_pages` (`page_id`, `page_title`, `slug`, `page_content`, `creator`, `created_date`) VALUES
(1, 'Giới Thiệu', 'gioi thieu', '<p style=\"text-align: justify;\"><b>C&ocirc;ng ty Cổ phần Đầu tư AUTOSMART</b>&nbsp;l&agrave; nền tảng b&aacute;n lẻ đa ng&agrave;nh nghề số 1 Việt Nam về doanh thu v&agrave; lợi nhuận.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Với chiến lược omni-channel, C&ocirc;ng ty vận h&agrave;nh mạng lưới h&agrave;ng ng&agrave;n cửa h&agrave;ng tr&ecirc;n to&agrave;n quốc song song với việc tận dụng hiểu biết s&acirc;u rộng về kh&aacute;ch h&agrave;ng th&ocirc;ng qua nền tảng dữ liệu lớn, năng lực chủ động triển khai c&aacute;c hoạt động hỗ trợ b&aacute;n lẻ được x&acirc;y dựng nội bộ v&agrave; li&ecirc;n tục đổi mới c&ocirc;ng nghệ nhằm&nbsp;tạo ra trải nghiệm kh&aacute;ch h&agrave;ng vượt trội v&agrave; thống nhất ở mọi k&ecirc;nh cũng như&nbsp;n&acirc;ng cao sự gắn kết của người ti&ecirc;u d&ugrave;ng với c&aacute;c thương hiệu của MWG.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Trong hệ sinh th&aacute;i của MWG, ngo&agrave;i c&aacute;c c&ocirc;ng ty con chuy&ecirc;n vận h&agrave;nh chuỗi b&aacute;n lẻ (thegioididong.com, Điện M&aacute;y Xanh, B&aacute;ch H&oacute;a Xanh, nh&agrave; thuốc An Khang...) c&ograve;n c&oacute; c&aacute;c c&ocirc;ng ty chuy&ecirc;n cung cấp dịch vụ c&oacute; li&ecirc;n quan như dịch vụ hậu m&atilde;i - bảo tr&igrave; - lắp đặt, dịch vụ giao h&agrave;ng chặng cuối, dịch vụ quản l&yacute; kho vận logistics, mảng ph&acirc;n phối sản phẩm n&ocirc;ng nghiệp an to&agrave;n 4KFarm...C&ocirc;ng ty cũng mở rộng kinh doanh ra thị trường nước ngo&agrave;i với li&ecirc;n doanh b&aacute;n lẻ điện m&aacute;y tại Indonesia.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><b>MWG</b>&nbsp;tập trung x&acirc;y dựng dịch vụ kh&aacute;ch h&agrave;ng kh&aacute;c biệt với chất lượng vượt trội, ph&ugrave; hợp với văn ho&aacute; đặt kh&aacute;ch h&agrave;ng l&agrave;m trung t&acirc;m trong mọi suy nghĩ v&agrave; h&agrave;nh động của c&ocirc;ng ty. 3 thương hiệu lớn của MWG đều nằm trong top 50 thương hiệu gi&aacute; trị nhất Việt Nam (The Brand Finance).</li>\r\n	<li style=\"text-align: justify;\">MWG&nbsp;nhiều năm liền c&oacute; t&ecirc;n trong c&aacute;c bảng xếp hạng danh gi&aacute; như TOP 500 nh&agrave; b&aacute;n lẻ h&agrave;ng đầu Ch&acirc;u &Aacute; &ndash; Th&aacute;i B&igrave;nh Dương (Retail Asia), TOP 50 c&ocirc;ng ty kinh doanh hiệu quả nhất Việt Nam (Nhịp Cầu Đầu Tư), TOP 50 c&ocirc;ng ty ni&ecirc;m yết tốt nhất Ch&acirc;u &Aacute; của tạp ch&iacute; uy t&iacute;n Forbes&hellip; Sự ph&aacute;t triển của MWG cũng l&agrave; một điển h&igrave;nh tốt được nghi&ecirc;n cứu tại c&aacute;c trường Đại học h&agrave;ng đầu như Harvard, UC Berkeley, trường kinh doanh Tuck (Mỹ).</li>\r\n	<li style=\"text-align: justify;\"><b>MWG</b>&nbsp;vinh dự khi l&agrave; đại diện Việt Nam duy nhất trong Top 100 nh&agrave; b&aacute;n lẻ h&agrave;ng đầu Ch&acirc;u &Aacute; &ndash; Th&aacute;i B&igrave;nh Dương do Tạp ch&iacute; b&aacute;n lẻ ch&acirc;u &Aacute; (Retail Asia) v&agrave; Tập đo&agrave;n nghi&ecirc;n cứu thị trường Euromonitor b&igrave;nh chọn.</li>\r\n	<li style=\"text-align: justify;\">Kh&ocirc;ng chỉ l&agrave; một doanh nghiệp hoạt động hiệu quả được nh&igrave;n nhận bởi nh&agrave; đầu tư v&agrave; c&aacute;c tổ chức đ&aacute;nh gi&aacute; chuy&ecirc;n nghiệp,&nbsp;<b>MWG</b>&nbsp;c&ograve;n được người lao động tin y&ecirc;u khi li&ecirc;n tiếp được vinh danh trong TOP 50 Doanh nghiệp c&oacute; m&ocirc;i trường l&agrave;m việc tốt nhất Việt Nam v&agrave; l&agrave; doanh nghiệp xuất sắc nhất tại giải thưởng Vietnam HR Awards &ndash; &ldquo;Chiến lược nh&acirc;n sự hiệu quả&rdquo;.</li>\r\n</ul>\r\n\r\n<p style=\"text-align: justify;\"><img data-original=\"https://mwg.vn/uploads/img/sites/4/pic_tgdd.png\" src=\"https://mwg.vn/uploads/img/sites/4/pic_tgdd.png\" />&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">khai trương chuỗi cửa h&agrave;ng đầu ti&ecirc;n v&agrave;o ng&agrave;y 22/10/2021. Đ&acirc;y l&agrave; một địa chỉ uy t&iacute;n chuy&ecirc;n cung cấp c&aacute;c sản phẩm Apple như: iPhone, iPad, Apple Watch, MacBook v&agrave; c&aacute;c phụ kiện Apple... Kh&ocirc;ng gian mua sắm tại TopZone được thiết kế đẳng cấp, hiện đại theo ti&ecirc;u chuẩn khắc khe của &ldquo;nh&agrave; T&aacute;o&rdquo;. T&iacute;nh đến thời điểm hiện tại, TopZone đ&atilde; ph&aacute;t triển nhanh ch&oacute;ng, đạt cột mốc ấn tượng hơn 50 cửa h&agrave;ng được khai trương tr&ecirc;n khắp cả nước.</p>\r\n', 'vuhonglinh123', '2023-09-10 17:44:00'),
(2, 'Liên hệ', 'liên hệ', '<p>C&Ocirc;NG TY TNHH THƯƠNG MẠI AUTOSAMRT</p>\r\n\r\n<p><b>Địa chỉ:</b>&nbsp;Trường cao đẳng Fpt Polytechnic</p>\r\n\r\n<p><b>Email:</b>&nbsp;<a href=\"mailto:tuyendung@gearvn.com\">tuyendung@autosmart.com</a></p>\r\n\r\n<p><b>Phone:</b>&nbsp;<a href=\"tel:18006147\">18006147</a></p>\r\n\r\n<h3>I. TRỤ SỞ CH&Iacute;NH</h3>\r\n\r\n<p>Trường cao đẳng Fpt Polytechnic</p>\r\n\r\n<h3>II. HỆ THỐNG SHOWROOM</h3>\r\n\r\n<p><a href=\"https://gearvn.com/pages/he-thong-showroom-gearvn\" target=\"_blank\">Xem to&agrave;n bộ cửa h&agrave;ng tr&ecirc;n to&agrave;n quốc</a></p>\r\n\r\n<h3>III. TRUNG T&Acirc;M BẢO H&Agrave;NH</h3>\r\n\r\n<p>Trường cao đẳng Fpt Polytechnic</p>\r\n', 'vuhonglinh123', '2023-09-10 18:23:51');
INSERT INTO `tb_posts` (`post_id`, `post_title`, `post_img`, `slug`, `status`, `created_date`, `creator`, `post_content`) VALUES
(3, 'Trên tay FIIL CC Pro 2: Tai nghe không dây với thiết kế độc đáo, pin dùng liên tục 8 tiếng cùng mức giá hấp dẫn', 'list_post2.jpg', 'Trên tay fiil cc pro 2', 'Đã đăng', '2023-12-07 18:47:30', 'vuhonglinh123', '<h3><strong>Tr&ecirc;n tay FIIL CC Pro 2 với thiết kế độc đ&aacute;o, mức độ ho&agrave;n thiện tốt</strong></h3>\r\n\r\n<p>Với sự ra mắt của rất nhiều sản phẩm tai nghe kh&ocirc;ng d&acirc;y tr&ecirc;n thị trường v&agrave; sự b&atilde;o ho&agrave; dần về kiểu d&aacute;ng thiết kế. FIIL CC Pro 2 đ&atilde; mang một l&agrave;n gi&oacute; mới trong ph&acirc;n kh&uacute;c khi sở hữu thiết kế rất lạ mắt.&nbsp;</p>\r\n\r\n<p><img alt=\"FIIL CC Pro 2 đã mang một làn gió mới trong phân khúc khi sở hữu thiết kế rất lạ mắt.\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/2-290823-071052-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/2-290823-071052-800-resize.jpg\" title=\"FIIL CC Pro 2 đã mang một làn gió mới trong phân khúc khi sở hữu thiết kế rất lạ mắt.\" /></p>\r\n\r\n<p>FIIL CC Pro 2 đ&atilde; mang một l&agrave;n gi&oacute; mới trong ph&acirc;n kh&uacute;c khi sở hữu thiết kế rất lạ mắt. Nguồn: Sean Talks Tech.</p>\r\n\r\n<p>Cụ thể, phần hộp sạc của thiết bị c&oacute; dạng h&igrave;nh chữ nhật vu&ocirc;ng vức với c&aacute;c g&oacute;c cạnh phẳng v&agrave; vu&ocirc;ng vức. Nhờ vậy, FIIL CC Pro 2 tr&ocirc;ng rất lạ mắt v&agrave; thu h&uacute;t khi so với c&aacute;c sản phẩm kh&aacute;c tr&ecirc;n thị trường. Phần hộp sạc cũng kh&ocirc;ng c&oacute; nắp đậy như thường lệ, điều n&agrave;y gi&uacute;p lấy tai nghe nhanh hơn nhưng cũng g&acirc;y n&ecirc;n một số lo lắng về độ bền.&nbsp;</p>\r\n\r\n<p><img alt=\"phần hộp sạc của thiết bị có dạng hình chữ nhật vuông vức với các góc cạnh phẳng và vuông vức.\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/5-290823-071209-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/5-290823-071209-800-resize.jpg\" title=\"phần hộp sạc của thiết bị có dạng hình chữ nhật vuông vức với các góc cạnh phẳng và vuông vức.\" /></p>\r\n\r\n<p>Phần hộp sạc của thiết bị c&oacute; dạng h&igrave;nh chữ nhật vu&ocirc;ng vức với c&aacute;c g&oacute;c cạnh phẳng v&agrave; vu&ocirc;ng vức. Nguồn: Sean Talks Tech.</p>\r\n\r\n<p>Hộp sạc c&oacute; k&iacute;ch thước 36.6 x 53.5 x 21.3 mm v&agrave; nặng 31 gram. Sản phẩm được nhận x&eacute;t l&agrave; mỏng, nhẹ v&agrave; rất dễ để bỏ v&agrave;o t&uacute;i v&agrave; mang theo b&ecirc;n người. YouTuber Sean Talks Tech đ&aacute;nh gi&aacute; cao thiết kế tổng thể n&agrave;y v&igrave; c&oacute; thể dễ d&agrave;ng bỏ thiết bị v&agrave;o t&uacute;i quần &aacute;o. Do kh&ocirc;ng sử dụng nắp đậy n&ecirc;n tai nghe FIIL CC Pro 2 được để lộ một phần ra ngo&agrave;i. C&aacute;ch lấy tai nghe ra cũng độc đ&aacute;o khi người d&ugrave;ng cần phải trượt l&ecirc;n tr&ecirc;n để sử dụng.&nbsp;</p>\r\n\r\n<p><img alt=\"Do không sử dụng nắp đậy nên tai nghe FIIL CC Pro 2 được để lộ một phần ra ngoài.\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/1-290823-072252-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/1-290823-072252-800-resize.jpg\" title=\"Do không sử dụng nắp đậy nên tai nghe FIIL CC Pro 2 được để lộ một phần ra ngoài.\" /></p>\r\n\r\n<p>Do kh&ocirc;ng sử dụng nắp đậy n&ecirc;n tai nghe FIIL CC Pro 2 được để lộ một phần ra ngo&agrave;i. Nguồn: Sean Talks Tech.</p>\r\n\r\n<p>Tai nghe c&oacute; dạng thanh với k&iacute;ch thước 34.2x 21.87 x 16.25 mm v&agrave; nặng 5.4 gram mỗi tai. K&iacute;ch thước n&agrave;y mang đến trải nghiệm đeo dễ chịu, thoải m&aacute;i. T&aacute;c giả đặc biệt th&iacute;ch phần ch&acirc;n đế của tai nghe khi được thiết kế nhỏ gọn v&agrave; đẹp mắt.&nbsp;</p>\r\n\r\n<p><img alt=\"Tai nghe có dạng thanh với kích thước 34.2x 21.87 x 16.25 mm và nặng 5.4 gram mỗi tai.\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/10-290823-072430-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/10-290823-072430-800-resize.jpg\" title=\"Tai nghe có dạng thanh với kích thước 34.2x 21.87 x 16.25 mm và nặng 5.4 gram mỗi tai.\" /></p>\r\n\r\n<p>Tai nghe c&oacute; dạng thanh với k&iacute;ch thước 34.2x 21.87 x 16.25 mm v&agrave; nặng 5.4 gram mỗi tai. Nguồn: Sean Talks Tech.</p>\r\n\r\n<p>Tai nghe d&ugrave;ng dạng in ear gi&uacute;p b&aacute;m tai tốt v&agrave; chống ồn hiệu quả hơn. N&uacute;t tai silicon cho cảm gi&aacute;c đeo mềm mại, b&aacute;m tai v&agrave; &uacute;t bị rơi khi vận động.&nbsp;</p>\r\n\r\n<h3><strong>FIIL CC Pro 2 cho chất &acirc;m tốt, hỗ trợ chống ồn</strong></h3>\r\n\r\n<p>FIIL CC Pro 2 sử dụng Bluetooth 5.3 để kết nối với điện thoại v&agrave; sử dụng app Fiil+ để thiết lập EQ. Việc kết nối diễn ra nhanh ch&oacute;ng với thao t&aacute;c đơn giản. C&aacute;c thao t&aacute;c thiết lập cũng nhanh ch&oacute;ng khi bạn c&oacute; thể chọn nhanh c&aacute;c chế độ sẵn c&oacute;.&nbsp;</p>\r\n\r\n<p><img alt=\"FIIL CC Pro 2 sử dụng Bluetooth 5.3 để kết nối với điện thoại và sử dụng app Fiil+ để thiết lập EQ.\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/14-290823-072517-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/14-290823-072517-800-resize.jpg\" title=\"FIIL CC Pro 2 sử dụng Bluetooth 5.3 để kết nối với điện thoại và sử dụng app Fiil+ để thiết lập EQ.\" /></p>\r\n\r\n<p>FIIL CC Pro 2 sử dụng Bluetooth 5.3 để kết nối với điện thoại v&agrave; sử dụng app Fiil+ để thiết lập EQ. Nguồn: Sean Talks Tech.</p>\r\n\r\n<p>Chất lượng &acirc;m thanh của FIIL CC Pro 2 được đ&aacute;nh gi&aacute; tốt trong ph&acirc;n kh&uacute;c. Tai nghe đạt mức tần số trong khoảng 20Hz~20kHz, thiết bị cũng đạt chứng nhận Hi-Res Audio Wireless gi&uacute;p người d&ugrave;ng y&ecirc;n t&acirc;m hơn về chất lượng &acirc;m thanh.&nbsp;</p>\r\n\r\n<p><img alt=\"Chất lượng âm thanh của FIIL CC Pro 2 được đánh giá tốt trong phân khúc. \" data-original=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/11-290823-072558-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/11-290823-072558-800-resize.jpg\" title=\"Chất lượng âm thanh của FIIL CC Pro 2 được đánh giá tốt trong phân khúc. \" /></p>\r\n\r\n<p>Chất lượng &acirc;m thanh của FIIL CC Pro 2 được đ&aacute;nh gi&aacute; tốt trong ph&acirc;n kh&uacute;c.&nbsp;</p>\r\n\r\n<p>FIIL CC Pro 2 cũng được trang bị t&iacute;nh năng chống ồn chủ động. Theo nh&agrave; sản xuất, thiết bị c&oacute; thể giảm tiếng ồn m&ocirc;i trường l&ecirc;n đến 99%, khử tiếng ồn tần số cao 94%.&nbsp;</p>\r\n\r\n<p><img alt=\"FIIL CC Pro 2 cũng được trang bị tính năng chống ồn chủ động.\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/15-290823-072711-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/15-290823-072711-800-resize.jpg\" title=\"FIIL CC Pro 2 cũng được trang bị tính năng chống ồn chủ động.\" /></p>\r\n\r\n<p>FIIL CC Pro 2 cũng được trang bị t&iacute;nh năng chống ồn chủ động. Nguồn: Sean Talks Tech.</p>\r\n\r\n<p>Theo nh&agrave; sản xuất, FIIL CC Pro 2 c&oacute; thời lượng pin khoảng 5 giờ khi bật chống ồn v&agrave; 8 giờ khi tắt. Đ&acirc;y l&agrave; một mức thời lượng pin rất tốt, mang đến cho người d&ugrave;ng thời gian sử dụng l&acirc;u d&agrave;i.&nbsp;</p>\r\n\r\n<h3><strong>FIIL CC Pro 2 gi&aacute; bao nhi&ecirc;u?</strong></h3>\r\n\r\n<p>FIIL CC Pro 2 hiện đang được b&aacute;n tại thị trường Malaysia với mức gi&aacute; 309 RM (khoảng 1.6 triệu đồng). Đ&acirc;y l&agrave; con số kh&aacute; dễ tiếp cận v&agrave; ph&ugrave; hợp với những t&iacute;nh năng m&agrave; thiết bị sở hữu.&nbsp;</p>\r\n\r\n<p><img alt=\"FIIL CC Pro 2 hiện đang được bán tại thị trường Malaysia với mức giá 309 RM (khoảng 1.6 triệu đồng).\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/13-290823-072822-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/25/1544392/13-290823-072822-800-resize.jpg\" title=\"FIIL CC Pro 2 hiện đang được bán tại thị trường Malaysia với mức giá 309 RM (khoảng 1.6 triệu đồng).\" /></p>\r\n\r\n<p>FIIL CC Pro 2 hiện đang được b&aacute;n tại thị trường Malaysia với mức gi&aacute; 309 RM (khoảng 1.6 triệu đồng). Nguồn: Sean Talks Tech.</p>\r\n\r\n<p>Vậy c&aacute;c bạn đ&aacute;nh gi&aacute; như thế n&agrave;o về FIIL CC Pro 2? H&atilde;y để lại l&yacute; kiến b&ecirc;n dưới cho ch&uacute;ng m&igrave;nh được biết nh&eacute;. Ngo&agrave;i ra, bạn c&oacute; thể tham khảo th&ecirc;m c&aacute;c sản phẩm tai nghe kh&ocirc;ng d&acirc;y ch&iacute;nh h&atilde;ng kh&aacute;c tại&nbsp;<a href=\"https://www.thegioididong.com/\" target=\"_blank\" title=\"Thế Giới Di Động\" type=\"Thế Giới Di Động\">Thế Giới Di Động</a>&nbsp;với mức gi&aacute; tốt c&ugrave;ng nhiều ưu đ&atilde;i hấp dẫn. Bạn c&oacute; thể tham khảo bằng c&aacute;ch bấm v&agrave;o n&uacute;t cam b&ecirc;n dưới nh&eacute;!</p>\r\n'),
(4, '[IFA 2023] JBL Soundgear Sense ra mắt với thiết kế tai mở, truyền âm thanh qua không khí', 'list_post3.jpg', 'JBL Soundgear Sense ra mắt với thiết kế tai mở', 'Đã đăng', '2023-11-10 11:09:23', 'vuhonglinh123', '<p>L&ecirc; Ho&agrave;i Nam</p>\r\n\r\n<p>&nbsp;1 giờ trước</p>\r\n\r\n<p><img alt=\"Thiết kế tai nghe JBL Soundgear Sense\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/04/1545637/thietketainghejblsoundgearsense-040923-080423-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/04/1545637/thietketainghejblsoundgearsense-040923-080423-800-resize.jpg\" title=\"Thiết kế tai nghe JBL Soundgear Sense\" /></p>\r\n\r\n<p>Thiết kế tai nghe JBL Soundgear Sense (Ảnh: JBL)</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/tai-nghe-jbl\" target=\"_blank\" title=\"JBL\" type=\"JBL\">JBL</a>&nbsp;vừa trở th&agrave;nh thương hiệu mới nhất tham gia v&agrave;o thị trường&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"tai nghe\" type=\"tai nghe\">tai nghe</a>&nbsp;sử dụng c&ocirc;ng nghệ dẫn truyền kh&ocirc;ng kh&iacute; với Soundgear Sense. Sản phẩm sở hữu thiết kế tai mở c&ugrave;ng với kỹ thuật đặc biệt cho ph&eacute;p truyền &acirc;m thanh v&agrave;o tai với mức độ r&ograve; rỉ &acirc;m thanh thấp m&agrave; kh&ocirc;ng cần nh&eacute;t tai nghe v&agrave;o trong tai.</h2>\r\n\r\n<p>Với c&ocirc;ng nghệ dẫn truyền &acirc;m thanh qua kh&ocirc;ng kh&iacute;, sản phẩm sẽ kh&ocirc;ng những vừa vặn m&agrave; c&ograve;n đem lại cảm gi&aacute;c thoải m&aacute;i cho người d&ugrave;ng khi đeo trong thời gian d&agrave;i. Ngo&agrave;i ra, tai nghe c&ograve;n gi&uacute;p người d&ugrave;ng c&oacute; thể nghe được c&aacute;c &acirc;m thanh quan trọng như tiếng xe cộ hay tiếng n&oacute;i chuyện trực tiếp.</p>\r\n\r\n<p><img alt=\"Thiết kế hộp sạc và tai nghe JBL Soundgear Sense\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/04/1545637/thietkehopsacvatainghejblsoundgearsense-040923-080723-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/04/1545637/thietkehopsacvatainghejblsoundgearsense-040923-080723-800-resize.jpg\" title=\"Thiết kế hộp sạc và tai nghe JBL Soundgear Sense\" /></p>\r\n\r\n<p>Thiết kế hộp sạc v&agrave; tai nghe JBL Soundgear Sense (Ảnh: JBL)&nbsp;&nbsp;</p>\r\n\r\n<p>Tai nghe mới của JBL sử dụng m&oacute;c tai c&oacute; thể điều chỉnh v&agrave; d&acirc;y đeo cổ c&oacute; thể th&aacute;o rời nhằm giữ ổn định vị tr&iacute; sản phẩm. Tr&igrave;nh điều khiển &acirc;m thanh 16.2 mm của JBL v&agrave; DSP gi&uacute;p tăng cường c&aacute;c &acirc;m trầm. Tai nghe vẫn sở hữu chuẩn kh&aacute;ng nước, kh&aacute;ng bụi IP54.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, mẫu tai nghe mới của JBL cũng hỗ trợ kết nối đa điểm cho ph&eacute;p kết nối đồng thời hai thiết bị như&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"điện thoại\" type=\"điện thoại\">điện thoại</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/laptop-ldp\" target=\"_blank\" title=\"laptop\" type=\"laptop\">laptop</a>. Thiết bị c&ograve;n c&oacute; bốn micro chịu tr&aacute;ch nhiệm thu &acirc;m v&agrave; loại bỏ tiếng ồn. Vi&ecirc;n pin tr&ecirc;n sản phẩm c&oacute; thể sử dụng trong 6 giờ v&agrave; 18 giờ khi kết hợp với hộp sạc.</p>\r\n\r\n<p><img alt=\"Phiên bản màu trắng của tai nghe JBL Soundgear Sense\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/04/1545637/phienbanmautrangcuatainghejblsoundgearsense-040923-080554-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/04/1545637/phienbanmautrangcuatainghejblsoundgearsense-040923-080554-800-resize.jpg\" title=\"Phiên bản màu trắng của tai nghe JBL Soundgear Sense\" /></p>\r\n\r\n<p>Phi&ecirc;n bản m&agrave;u trắng của tai nghe JBL Soundgear Sense (Ảnh: JBL)</p>\r\n\r\n<p>JBL cũng sẽ ph&aacute;t h&agrave;nh bản cập nhật hỗ trợ kết nối&nbsp;Bluetooth LE cho sản phẩm. Tai nghe&nbsp;JBL Soundgear Sense sẽ được b&aacute;n lẻ với gi&aacute; 149.99 EUR (khoảng 3.9 triệu đồng) tại c&aacute;c trang web ch&iacute;nh thức của JBL.</p>\r\n'),
(5, 'Cách thanh toán tiền xe bằng Apple Pay thuận tiện hơn và tiết kiệm thời gian dơn', 'list_post4.jpg', 'Cách thanh toán tiền xe bằng Apple Pay', 'Đã đăng', '2023-11-10 11:09:23', 'vuhonglinh123', '<p><img alt=\"Cách thanh toán tiền xe bằng Apple Pay\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/24/1544180/cach-dat-xe-cong-nghe-bang-apple-pay-240823-035019-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/24/1544180/cach-dat-xe-cong-nghe-bang-apple-pay-240823-035019-800-resize.jpg\" title=\"Cách thanh toán tiền xe bằng Apple Pay\" /></p>\r\n\r\n<h2>Apple Pay l&agrave; một ứng dụng thanh to&aacute;n tiện &iacute;ch v&agrave; bảo mật tr&ecirc;n c&aacute;c thiết bị của Apple, cho ph&eacute;p bạn thực hiện c&aacute;c giao dịch mua sắm, đặt v&eacute; v&agrave; nhiều dịch vụ kh&aacute;c như l&agrave; đặt xe chỉ bằng một c&uacute; chạm ngay tr&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"iPhone\" type=\"iPhone\">iPhone</a>&nbsp;của bạn. Nếu bạn muốn biết&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/cach-thanh-toan-tien-xe-bang-apple-pay-1544180\" target=\"_blank\" title=\"Cách thanh toán tiền xe bằng Apple Pay\" type=\"Cách thanh toán tiền xe bằng Apple Pay\">c&aacute;ch&nbsp;thanh to&aacute;n tiền xe bằng Apple Pay</a>&nbsp;th&igrave; h&atilde;y c&ugrave;ng theo d&otilde;i b&agrave;i viết v&agrave; l&agrave;m theo c&aacute;c bước sau đ&acirc;y nh&eacute;.</h2>\r\n\r\n<h3><strong>C&aacute;ch thanh to&aacute;n tiền xe bằng Apple Pay</strong></h3>\r\n\r\n<p>Hiện tại trong thời gian m&igrave;nh thực hiện b&agrave;i viết n&agrave;y chỉ c&oacute; mỗi Be hỗ trợ dịch vụ của m&igrave;nh với Apple Pay, trong tương lai hi vọng rằng c&aacute;c ứng dụng tương tự cũng sẽ hỗ trợ bạn c&oacute; thể tham khảo v&agrave; thực hiện.</p>\r\n\r\n<p><b>Bước 1: Đầu ti&ecirc;n bạn h&atilde;y mở ứng dụng Be l&ecirc;n&nbsp;</b>&gt; tiếp theo sau đ&oacute; bạn h&atilde;y bấm chọn dịch vụ đặt xe m&agrave; bạn cần d&ugrave;ng&nbsp;&gt; sau đ&oacute; bạn h&atilde;y bấm chọn tuỳ chỉnh phương thức thanh to&aacute;n.</p>\r\n\r\n<p><img alt=\"Cách thanh toán tiền xe bằng Apple Pay\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/24/1544180/choose-payment-method-240823-035021-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/24/1544180/choose-payment-method-240823-035021-800-resize.jpg\" title=\"Cách thanh toán tiền xe bằng Apple Pay\" /></p>\r\n\r\n<p><b>Bước 2: L&uacute;c n&agrave;y, tại phần h&igrave;nh thức thanh to&aacute;n&nbsp;</b>&gt; bạn h&atilde;y bấm chọn h&igrave;nh thức thanh to&aacute;n l&agrave; Apple Pay&nbsp;&gt; tiếp theo bạn h&atilde;y bấm t&iacute;ch v&agrave;o phần Apple Pay như h&igrave;nh b&ecirc;n phải ph&iacute;a b&ecirc;n dưới&nbsp;&gt; b&acirc;y giờ th&igrave; bạn chỉ cần tuỳ chọn m&atilde; khuyến m&atilde;i rồi đặt xe th&ocirc;i.</p>\r\n\r\n<p><img alt=\"Cách thanh toán tiền xe bằng Apple Pay\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/24/1544180/book-car-240823-035022-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/24/1544180/book-car-240823-035022-800-resize.jpg\" title=\"Cách thanh toán tiền xe bằng Apple Pay\" /></p>\r\n\r\n<p>Như vậy, m&igrave;nh đ&atilde; chia sẻ c&aacute;ch&nbsp;thanh to&aacute;n tiền xe bằng Apple Pay đến với mọi người.&nbsp;Hy vọng b&agrave;i viết gi&uacute;p &iacute;ch được cho mọi người.&nbsp;Nếu thấy hay th&igrave; h&atilde;y chia sẻ b&agrave;i viết v&agrave; b&igrave;nh luận ở ph&iacute;a dưới để c&ugrave;ng nhau trao đổi nhiều hơn nh&eacute;.</p>\r\n\r\n<p>Những chiếc điện thoại iPhone thời thượng v&agrave; đẳng cấp lu&ocirc;n l&agrave; lựa chọn h&agrave;ng đầu của d&acirc;n y&ecirc;u c&ocirc;ng nghệ. Để sở hữu cho m&igrave;nh những chiếc iPhone ch&iacute;nh h&atilde;ng v&agrave; tha hồ lựa chọn m&agrave;u sắc, dung lượng bộ nhớ th&igrave; c&aacute;c bạn đừng qu&ecirc;n chuỗi cửa h&agrave;ng Thế Giới Di Động nh&eacute;! Nhấn v&agrave;o n&uacute;t cam b&ecirc;n dưới để chọn mua ngay!</p>\r\n'),
(6, 'Magic Keyboard mới cho iPad Pro 2024 sẽ có lớp viền bằng nhôm sang trọng', 'list_post5.jpg', 'Magic Keyboard mới cho iPad Pro 2024', 'Đã đăng', '2023-11-10 11:09:23', 'vuhonglinh123', '<p><img alt=\"Magic Keyboard mới cho iPad Pro sẽ được trang bị lớp viền bằng nhôm sang trọng\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/03/1545609/134-030923-221705-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/03/1545609/134-030923-221705-800-resize.jpg\" title=\"Magic Keyboard mới cho iPad Pro sẽ được trang bị lớp viền bằng nhôm sang trọng\" /></p>\r\n\r\n<p>Magic Keyboard mới cho iPad Pro sẽ được trang bị lớp viền bằng nh&ocirc;m sang trọng</p>\r\n\r\n<h2>Trong tuần trước, chuy&ecirc;n gia Mark Gurman từ Bloomberg đ&atilde; tiết lộ rằng,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-pro-m2\" target=\"_blank\" title=\"iPad Pro\" type=\"iPad Pro\">iPad Pro</a>&nbsp;thế hệ tiếp theo của&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Apple \" type=\"Apple \">Apple</a>&nbsp;sẽ ra mắt c&ugrave;ng với một phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/ban-phim-magic-keyboard-2-ipad-pro-11-mxqt2\" target=\"_blank\" title=\"Magic Keyboard\" type=\"Magic Keyboard\">Magic Keyboard</a>&nbsp;ho&agrave;n to&agrave;n mới, được thiết kế lại đặc biệt, gi&uacute;p thiết bị&nbsp;giống như&nbsp;<a href=\"https://www.thegioididong.com/laptop-ldp\" target=\"_blank\" title=\"laptop\" type=\"laptop\">laptop</a>&nbsp;hơn. V&agrave; h&ocirc;m nay, th&ocirc;ng tin về&nbsp;chi tiết về việc thiết kế lại phụ kiện n&agrave;y tiếp tục bị r&ograve; rỉ.</h2>\r\n\r\n<p>Cụ thể, khu vực xung quanh b&agrave;n ph&iacute;m sẽ được l&agrave;m bằng nh&ocirc;m, tương tự như phần tr&ecirc;n của c&aacute;c d&ograve;ng MacBook, nhằm mang lại một cấu tr&uacute;c chắc chắn hơn. Apple hy vọng rằng những thay đổi n&agrave;y sẽ giải quyết c&aacute;c vấn đề của phi&ecirc;n bản hiện tại m&agrave; người d&ugrave;ng đ&atilde; phản &aacute;nh.</p>\r\n\r\n<p><img alt=\"Apple hy vọng rằng Magic Keyboard mới cho iPad Pro 2024 sẽ giải quết được các vấn đề hiện tại\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/03/1545609/135-030923-222507-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/03/1545609/135-030923-222507-800-resize.jpg\" title=\"Apple hy vọng rằng Magic Keyboard mới cho iPad Pro 2024 sẽ giải quết được các vấn đề hiện tại\" /></p>\r\n\r\n<p>Apple hy vọng rằng Magic Keyboard mới cho iPad Pro 2024 sẽ giải quyết được c&aacute;c vấn đề hiện tại (Ảnh: Engadget)</p>\r\n\r\n<p>Vỏ ngo&agrave;i của b&agrave;n ph&iacute;m sẽ tiếp tục sử dụng chất liệu silicone bảo vệ, giống như phi&ecirc;n bản hiện tại, đồng thời được trang bị một cổng USB-C duy nhất. Tuy nhi&ecirc;n, Gurman cho rằng, việc sử dụng c&aacute;c vật liệu cao cấp hơn trong thiết kế mới c&oacute; thể khiến Apple tăng gi&aacute; phụ kiện n&agrave;y l&ecirc;n tới 299 USD (khoảng 7.2 triệu đồng).</p>\r\n\r\n<p>Dự kiến, b&agrave;n ph&iacute;m Magic Keyboard được thiết kế lại sẽ ra mắt c&ugrave;ng với c&aacute;c mẫu iPad Pro thế hệ tiếp theo v&agrave;o năm sau. Theo Gurman, những mẫu iPad Pro mới n&agrave;y sẽ mang đến những &quot;thay đổi cơ bản&quot; v&agrave; đ&aacute;nh dấu sự cập nhật lớn đầu ti&ecirc;n kể từ năm 2018. D&ograve;ng sản phẩm n&agrave;y sẽ trang bị vi xử l&yacute; Apple Silicon M3 mạnh mẽ, m&agrave;n h&igrave;nh OLED v&agrave; t&ugrave;y chọn k&iacute;ch thước 11 v&agrave; 13 inch lớn hơn một ch&uacute;t.</p>\r\n\r\n<p>Bạn c&oacute; mong đợi điều g&igrave; về chiếc&nbsp;iPad Pro 2024 hay kh&ocirc;ng?</p>\r\n'),
(7, '24h công nghệ có gì HOT 3/9: Xiaomi 13T Pro sẽ ra mắt trong tháng này, BXH smartphone Android mạnh nhất tháng 8', 'list_post6.jpg', '24h công nghệ có gì HOT 3/9', 'Đã đăng', '2023-11-10 11:09:23', 'vuhonglinh123', '<p><img alt=\"24h công nghệ có gì HOT 3/9\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/03/1545625/24h-cong-nghe-3-9-2023-040923-002553-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/03/1545625/24h-cong-nghe-3-9-2023-040923-002553-800-resize.jpg\" title=\"24h công nghệ có gì HOT 3/9\" /></p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/tin-tuc/24h-cong-nghe-co-gi-hot-3-9-1545625\" target=\"_blank\" title=\"24h công nghệ có gì HOT 3/9\" type=\"24h công nghệ có gì HOT 3/9\">24h&nbsp;c&ocirc;ng nghệ c&oacute; g&igrave; HOT 3/9</a>&nbsp;với những th&ocirc;ng tin đ&aacute;ng ch&uacute; &yacute; như:&nbsp;Xiaomi 13T Pro sẽ ra mắt trong th&aacute;ng n&agrave;y,&nbsp;BXH smartphone Android mạnh nhất th&aacute;ng 8,...</h2>\r\n\r\n<h3><strong>1.&nbsp;Xiaomi 13T Pro sẽ ra mắt trong th&aacute;ng n&agrave;y</strong></h3>\r\n\r\n<p><img alt=\"Xiaomi 13T Pro được đồn sẽ ra mắt trong tháng 9\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/03/1545565/xiaomi12tpro-9-030923-130522-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/03/1545565/xiaomi12tpro-9-030923-130522-800-resize.jpg\" /></p>\r\n\r\n<p>Sau khi ra mắt d&ograve;ng&nbsp;<a href=\"https://thegioididong.com/dtdd/xiaomi-13\" target=\"_blank\" title=\"Xiaomi 13\" type=\"Xiaomi 13\">Xiaomi 13</a>,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Xiaomi \" type=\"Xiaomi \">Xiaomi</a>&nbsp;được cho l&agrave; đang ph&aacute;t triển&nbsp;<a href=\"https://www.thegioididong.com/dtdd/xiaomi-13t\" target=\"_blank\" title=\"Xiaomi 13T\" type=\"Xiaomi 13T\">Xiaomi 13T</a>&nbsp;v&agrave; Xiaomi 13T Pro. Gần đ&acirc;y,&nbsp;<a href=\"http://www.thegioididong.com/dtdd/xiaomi-13t-pro\" target=\"_blank\" title=\"Xiaomi 13T Pro\" type=\"Xiaomi 13T Pro\">Xiaomi 13T Pro</a>&nbsp;đ&atilde; được ph&aacute;t hiện tr&ecirc;n trang chứng chỉ NCC c&ugrave;ng với nhiều chứng chỉ kh&aacute;c như FCC, NBTC, Geekbench, IMDA, EEC, SIRIM v&agrave; TDRA.</p>\r\n\r\n<p>Xem tiếp:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/xiaomi-13t-pro-se-ra-mat-trong-thang-nay-1545565\" target=\"_blank\" title=\"Xiaomi 13T Pro sẽ ra mắt trong tháng này, cấu hình và thiết kế đã bị lộ\" type=\"Xiaomi 13T Pro sẽ ra mắt trong tháng này, cấu hình và thiết kế đã bị lộ\">Xiaomi 13T Pro sẽ ra mắt trong th&aacute;ng n&agrave;y, cấu h&igrave;nh v&agrave; thiết kế đ&atilde; lộ</a></p>\r\n\r\n<h3><strong>2.&nbsp;BXH smartphone Android mạnh nhất th&aacute;ng 8</strong></h3>\r\n\r\n<p><img alt=\"Hai smartphone Android mạnh nhất tháng 8 theo AnTuTu công bố\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/02/1545513/antutu_benchmark1-020923-203712-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/02/1545513/antutu_benchmark1-020923-203712-800-resize.jpg\" /></p>\r\n\r\n<p>AnTuTu mới đ&acirc;y đ&atilde; c&ocirc;ng bố bảng xếp hạng c&aacute;c mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Android\" type=\"Android\">Android</a>&nbsp;cận cao cấp v&agrave; tầm trung mạnh mẽ nhất của th&aacute;ng 8, một th&aacute;ng chứng kiến sự ra mắt của nhiều c&aacute;i t&ecirc;n đ&igrave;nh đ&aacute;m. Sau đ&acirc;y, h&atilde;y c&ugrave;ng xem qua bảng xếp hạng&nbsp;n&agrave;y nh&eacute;!</p>\r\n\r\n<p>Xem tiếp:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/bang-xep-hang-smartphone-android-thang-8-cua-antutu-1545513\" target=\"_blank\" title=\"BXH smartphone Android tháng 8 của AnTuTu: Chip Snapdragon tiếp tục dẫn đầu\" type=\"BXH smartphone Android tháng 8 của AnTuTu: Chip Snapdragon tiếp tục dẫn đầu\">Bảng xếp hạng smartphone Android th&aacute;ng 8 AnTuTu: Chip Rồng vẫn dẫn đầu</a></p>\r\n\r\n<h3><strong>3.&nbsp;Pixel 8 Pro sẽ c&oacute; t&iacute;nh năng quay video mới</strong></h3>\r\n\r\n<p><img alt=\"Pixel 8 Pro sẽ có tính năng quay video mới đáng để chờ đợi\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/03/1545561/2-030923-121722-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/03/1545561/2-030923-121722-800-resize.jpg\" /></p>\r\n\r\n<p>Gần đ&acirc;y c&oacute; nhiều đồn đo&aacute;n rằng d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd/google-pixel-8\" target=\"_blank\" title=\"Pixel 8\" type=\"Pixel 8\">Pixel 8</a>&nbsp;c&oacute; thể loại bỏ khay&nbsp;<a href=\"https://thegioididong.com/sim-so-dep\" target=\"_blank\" title=\"SIM\" type=\"SIM\">SIM</a>&nbsp;v&agrave; chuyển sang eSIM, c&aacute;c nguồn th&ocirc;ng tin đ&atilde; cho biết những tin đồn đ&oacute; l&agrave; kh&ocirc;ng c&oacute; căn cứ. D&ograve;ng Pixel 8 sẽ giữ nguy&ecirc;n khay SIM vật l&yacute;,&nbsp;<a href=\"https://www.thegioididong.com/dtdd/google-pixel-8-pro\" target=\"_blank\" title=\"Pixel 8\" type=\"Pixel 8\">Pixel 8 Pro</a>&nbsp;sẽ c&oacute; th&ecirc;m t&iacute;nh năng quay video Night Sight.</p>\r\n\r\n<p>Xem tiếp:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/pixel-8-pro-se-co-tinh-nang-quay-video-moi-1545561\" target=\"_blank\" title=\"Pixel 8 Pro sẽ có tính năng quay video mới đáng để chờ đợi\" type=\"Pixel 8 Pro sẽ có tính năng quay video mới đáng để chờ đợi\">Pixel 8 Pro sẽ c&oacute; t&iacute;nh năng quay video mới đ&aacute;ng để chờ đợi</a></p>\r\n\r\n<h3><strong>4.&nbsp;Đ&aacute;nh gi&aacute; hiệu năng realme 11 Pro+ 5G</strong></h3>\r\n\r\n<p><img alt=\"realme 11 Pro+ 5G với Dimensity 7050 không mang đến một hiệu năng quá mạnh mẽ nhưng cân bằng tốt ở nhiều mặt\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/23/1544013/realme-11-pro-plus-3-230823-144219-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/23/1544013/realme-11-pro-plus-3-230823-144219-800-resize.jpg\" /></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/realme-11-pro-plus-5g\" target=\"_blank\" title=\"realme 11 Pro+ 5G\" type=\"realme 11 Pro+ 5G\">realme 11 Pro+ 5G</a>&nbsp;được trang bị con chip Dimensity 7050 của nh&agrave; MediaTek. Được biết, Dimensity 7050 l&agrave; phi&ecirc;n bản được đổi thương hiệu của Dimensity 1080, sử dụng tiến tr&igrave;nh 6 nm n&ecirc;n cho hiệu năng ấn tượng cũng như rất tiết kiệm năng lượng.</p>\r\n\r\n<p>Xem tiếp:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/danh-gia-hieu-nang-realme-11-pro-plus-5g-1544013\" target=\"_blank\" title=\"Đánh giá hiệu năng realme 11 Pro+ 5G: Chơi game mượt mà với chip Dimensity 7050, thời lượng pin lâu dài\" type=\"Đánh giá hiệu năng realme 11 Pro+ 5G: Chơi game mượt mà với chip Dimensity 7050, thời lượng pin lâu dài\">Đ&aacute;nh gi&aacute; hiệu năng realme 11 Pro+ 5G: Chiến game mượt, pin tối ưu tốt</a></p>\r\n\r\n<h3><strong>5.&nbsp;C&aacute;ch chỉnh h&igrave;nh nền cuộc gọi tr&ecirc;n m&agrave;n h&igrave;nh phụ Galaxy Z Flip5</strong></h3>\r\n\r\n<p><img alt=\"Cách chỉnh hình nền cuộc gọi trên màn hình phụ Galaxy Z Flip5\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/18/1543360/hinhnencuocgoi3-270823-165937-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/18/1543360/hinhnencuocgoi3-270823-165937-800-resize.jpg\" /></p>\r\n\r\n<p>Tương tự như tr&ecirc;n m&agrave;n h&igrave;nh ch&iacute;nh th&igrave; với m&agrave;n h&igrave;nh phụ n&agrave;y bạn cũng c&oacute; thể điều chỉnh h&igrave;nh nền cuộc gọi để n&oacute; hiển thị l&ecirc;n đẹp hơn. Ngay sau đ&acirc;y l&agrave; c&aacute;ch chỉnh h&igrave;nh nền cuộc gọi tr&ecirc;n m&agrave;n h&igrave;nh phụ&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-flip5\" target=\"_blank\" title=\"Galaxy Z Flip5\" type=\"Galaxy Z Flip5\">Galaxy Z Flip5</a>, xem nh&eacute;.</p>\r\n\r\n<p>Xem tiếp:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/cach-chinh-hinh-nen-cuoc-goi-tren-man-hinh-phu-galaxy-z-flip5-1543360\" target=\"_blank\" title=\"Cách chỉnh hình nền cuộc gọi trên màn hình phụ Galaxy Z Flip5 siêu đẹp, bạn nên thử\" type=\"Cách chỉnh hình nền cuộc gọi trên màn hình phụ Galaxy Z Flip5 siêu đẹp, bạn nên thử\">C&aacute;ch chỉnh h&igrave;nh nền cuộc gọi tr&ecirc;n m&agrave;n h&igrave;nh phụ Galaxy Z Flip5 cực đẹp</a></p>\r\n\r\n<h3><strong>6.&nbsp;Galaxy Tab S9 Ultra xứng tầm tablet cao cấp</strong></h3>\r\n\r\n<p><img alt=\"Đặc biệt là tính năng GoodNotes lần đầu xuất hiện trên Android\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/12/1542345/galaxytabs9ultralifestyle-36-120823-215835-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/12/1542345/galaxytabs9ultralifestyle-36-120823-215835-800-resize.jpg\" /></p>\r\n\r\n<p>H&atilde;y c&ugrave;ng m&igrave;nh (một Content Editor) t&igrave;m hiểu một ng&agrave;y sử dụng v&agrave; l&agrave;m việc c&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/samsung-galaxy-tab-s9-ultra\" target=\"_blank\" title=\"Galaxy Tab S9 Ultra\" type=\"Galaxy Tab S9 Ultra\">Galaxy Tab S9 Ultra</a>&nbsp;th&uacute; vị như thế n&agrave;o nh&eacute;!</p>\r\n');
INSERT INTO `tb_posts` (`post_id`, `post_title`, `post_img`, `slug`, `status`, `created_date`, `creator`, `post_content`) VALUES
(8, 'Đánh giá Sony Xperia 5 V: Thiết kế thay đổi, camera cải tiến độ phân giải, chip Snapdragon 8 Gen 2 mạnh mẽ', 'list_post7.jpg', 'Đánh giá Sony Xperia 5 V', 'Đã đăng', '2023-10-09 17:23:03', 'vuhonglinh123', '<h3><strong>Thiết kế Xperia 5 V thay đổi theo hướng trẻ trung nhưng vẫn c&oacute; n&eacute;t đặc trưng</strong></h3>\r\n\r\n<p>Nhắc đến c&aacute;c thiết bị smartphone đến từ Sony, ch&uacute;ng ta đ&atilde; qu&aacute; quen thuộc với phong c&aacute;ch thiết kế OmniBalance l&agrave;m n&ecirc;n thương hiệu v&agrave; Xperia 5 V vẫn được thừa hưởng những g&igrave; tuyệt vời nhất trong phong c&aacute;ch thiết kế n&agrave;y với độ ho&agrave;n thiện cao v&agrave; cảm gi&aacute;c cầm nắm thoải m&aacute;i. Về mặt tổng thể, tuy thiết kế của Xperia 5 V kh&ocirc;ng thay đổi qu&aacute; nhiều so với thế hệ tiền nhiệm nhưng đ&acirc;y lại l&agrave; một n&eacute;t đặc trưng ri&ecirc;ng của ri&ecirc;ng Sony, chủ yếu tập trung v&agrave;o sự đơn giản v&agrave; cải thiện trải nghiệm sử dụng.</p>\r\n\r\n<p><img alt=\"Xperia 5 V (phải) được thiết kế theo phong cách OmniBalance quen thuộc\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/8-020923-003651-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/8-020923-003651-800-resize.jpg\" title=\"Xperia 5 V (phải) được thiết kế theo phong cách OmniBalance quen thuộc\" /></p>\r\n\r\n<p>Xperia 5 V (phải) được thiết kế theo phong c&aacute;ch OmniBalance quen thuộc. Nguồn: GSMArena.</p>\r\n\r\n<p>Xperia 5 V sở hữu th&acirc;n h&igrave;nh cực nhỏ gọn với k&iacute;ch thước&nbsp;154 x 68 x 8.6 mm&nbsp;v&agrave; chỉ nặng 183 g, lớn hơn một ch&uacute;t so với thế hệ tiền nhiệm Xperia 5 IV. Đồng thời,&nbsp;thiết bị vẫn giữ nguy&ecirc;n vị tr&iacute; tổ hợp n&uacute;t vật l&yacute; ở cạnh phải bao gồm n&uacute;t tăng/giảm &acirc;m lượng, n&uacute;t nguồn c&oacute; cảm biến v&acirc;n tay cực nhạy v&agrave; một n&uacute;t bấm hỗ trợ chụp ảnh rất tiện lợi chỉ c&oacute; tr&ecirc;n c&aacute;c d&ograve;ng điện thoại Xperia. Hơn nữa, cạnh tr&ecirc;n của Xperia 5 V vẫn c&oacute; sự xuất hiện của cổng tai nghe 3.5 mm gi&uacute;p người d&ugrave;ng c&oacute; thể nghe nhạc với mọi loại tai nghe m&agrave; kh&ocirc;ng cần phải mua th&ecirc;m c&aacute;c loại c&aacute;p chuyển đổi cho c&aacute;c cổng kết nối kh&aacute;c nhau.</p>\r\n\r\n<p><img alt=\"Cạnh phải của Xperia 5 V với nút nguồn, nút tăng/giảm âm lượng và nút khởi động máy ảnh\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/13-020923-003911-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/13-020923-003911-800-resize.jpg\" title=\"Cạnh phải của Xperia 5 V với nút nguồn, nút tăng/giảm âm lượng và nút khởi động máy ảnh\" /></p>\r\n\r\n<p>Cạnh phải của Xperia 5 V với n&uacute;t nguồn, n&uacute;t tăng/giảm &acirc;m lượng v&agrave; n&uacute;t khởi động m&aacute;y ảnh. Nguồn: GSMArena.</p>\r\n\r\n<p><img alt=\"Cạnh dưới của Xperia 5 V có khay SIM, cổng sạc, buồng loa và mic thoại\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/12-020923-003904-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/12-020923-003904-800-resize.jpg\" title=\"Cạnh dưới của Xperia 5 V có khay SIM, cổng sạc, buồng loa và mic thoại\" /></p>\r\n\r\n<p>Cạnh dưới của Xperia 5 V c&oacute; khay SIM, cổng sạc v&agrave; mic thoại. Nguồn: GSMArena.</p>\r\n\r\n<p><img alt=\"Cạnh trên của Xperia 5 V có cổng tai nghe 3.5 mm\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/11-020923-003852-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/11-020923-003852-800-resize.jpg\" title=\"Cạnh trên của Xperia 5 V có cổng tai nghe 3.5 mm\" /></p>\r\n\r\n<p>Cạnh tr&ecirc;n của Xperia 5 V c&oacute; cổng tai nghe 3.5 mm. Nguồn: GSMArena.</p>\r\n\r\n<p>Cả hai mặt trước, sau của&nbsp;Xperia 5 V đều được bảo vệ bởi hai tấm k&iacute;nh cường lực Gorilla Glass Victus thế hệ 2, đi k&egrave;m c&ugrave;ng một khung viền vu&ocirc;ng vức được l&agrave;m từ kim loại chắc chắn. Kh&ocirc;ng những thế, thiết bị c&ograve;n đạt chuẩn kh&aacute;ng nước IP65/IP68 gi&uacute;p hạn chế hư hỏng trong m&ocirc;i trường ẩm hoặc khi tiếp x&uacute;c với nước v&agrave; bụi.</p>\r\n\r\n<p><img alt=\"Xperia 5 V được trang bị kính Gorilla Glass Victus 2 và đạt chuẩn kháng nước IP65/IP68\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/15-020923-004530-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/15-020923-004530-800-resize.jpg\" title=\"Xperia 5 V được trang bị kính Gorilla Glass Victus 2 và đạt chuẩn kháng nước IP65/IP68\" /></p>\r\n\r\n<p>Xperia 5 V được trang bị k&iacute;nh Gorilla Glass Victus 2 v&agrave; đạt chuẩn kh&aacute;ng nước IP65/IP68. Nguồn: GSMArena.</p>\r\n\r\n<p>Tại khu vực camera, bộ đ&ocirc;i ống k&iacute;nh k&eacute;p của Xperia 5 V c&oacute; khung viền được thiết kế theo dạng h&igrave;nh oval k&eacute;o d&agrave;i c&ugrave;ng đ&egrave;n flash LED, micr&ocirc; v&agrave; logo Zeiss. Tuy hơi đơn giản nhưng đ&acirc;y lại ch&iacute;nh l&agrave; điểm nhấn nổi bật nhất trong kh&acirc;u thiết kế mặt lưng của m&aacute;y.</p>\r\n\r\n<p><img alt=\"Cụm camera của Xperia 5 V được thiết kế lại trông rất đẹp mắt\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/18-020923-004654-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/18-020923-004654-800-resize.jpg\" title=\"Cụm camera của Xperia 5 V được thiết kế lại trông rất đẹp mắt\" /></p>\r\n\r\n<p>Cụm camera của Xperia 5 V được thiết kế lại tr&ocirc;ng rất đẹp mắt. Nguồn: GSMArena.</p>\r\n\r\n<p>Quay sang mặt trước, Xperia 5 V sở hữu m&agrave;n h&igrave;nh 6.1 inch với độ ph&acirc;n giải&nbsp;Full HD+ (1.080 x 2.520 pixels) v&agrave; sử dụng tấm nền OLED với tỷ lệ 21:9. Về tổng thể, thiết bị vẫn sử dụng thiết kế m&agrave;n h&igrave;nh kiểu truyền thống với hai cạnh tr&ecirc;n dưới d&agrave;y nhưng với tỷ lệ 21:9, khung h&igrave;nh vẫn sẽ hiển thị đầy đủ h&igrave;nh ảnh v&agrave; ho&agrave;n to&agrave;n c&oacute; thể đem đến cho bạn những trải nghiệm sử dụng đa phương tiện rất tốt, đặc biệt trong khi xem phim hay chơi game.</p>\r\n\r\n<p><img alt=\"Xperia 5 V được trang bị màn hình OLED rộng 6.1 inch, độ phân giải Full HD+\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/14-020923-172325-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/14-020923-172325-800-resize.jpg\" title=\"Xperia 5 V được trang bị màn hình OLED rộng 6.1 inch, độ phân giải Full HD+\" /></p>\r\n\r\n<p>Xperia 5 V được trang bị m&agrave;n h&igrave;nh OLED rộng 6.1 inch, độ ph&acirc;n giải Full HD+. Nguồn: GSMArena.</p>\r\n\r\n<p>Đồng thời,&nbsp;tấm nền OLED của Xperia 5 V c&ograve;n c&oacute; độ s&aacute;ng tối đa ở mức 875 nits (chế độ s&aacute;ng tự động), đi k&egrave;m với độ s&acirc;u m&agrave;u 10 bit v&agrave; đạt chuẩn HDR10 n&ecirc;n những h&igrave;nh cho ra&nbsp;lu&ocirc;n c&oacute; m&agrave;u sắc sống động c&ugrave;ng độ b&atilde;o h&ograve;a cao. Thiết bị cũng hỗ trợ tốc độ l&agrave;m mới 120 Hz v&agrave; kh&ocirc;ng ti&ecirc;u tốn qu&aacute; nhiều điện năng trong qu&aacute; tr&igrave;nh sử dụng. Cụ thể, Xperia 5 V lu&ocirc;n hoạt động ở&nbsp;mức tần số qu&eacute;t 120 Hz hầu hết t&aacute;c vụ v&agrave; chỉ trở lại 60 Hz khi xem video hoặc c&aacute;c ứng dụng kh&ocirc;ng hỗ trợ tần số qu&eacute;t cao.</p>\r\n\r\n<h3><strong>Xperia 5 V camera 48 MP cho chất lượng ảnh chụp ấn tượng</strong></h3>\r\n\r\n<p>Một trong những n&acirc;ng cấp nổi bật m&agrave; Sony d&agrave;nh cho Xperia 5 V đ&oacute; ch&iacute;nh l&agrave; hệ thống camera. Thiết bị mang tr&ecirc;n m&igrave;nh camera k&eacute;p với cảm biến ch&iacute;nh c&oacute; độ ph&acirc;n giải 48 MP (cao hơn mức 12 MP tr&ecirc;n thế hệ tiền nhiệm&nbsp;Xperia 5 IV), đi k&egrave;m c&ugrave;ng cảm biến chụp g&oacute;c si&ecirc;u rộng v&agrave; camera selfie ở mặt trước với th&ocirc;ng số cụ thể như sau:</p>\r\n\r\n<ul>\r\n	<li>Camera ch&iacute;nh: Độ ph&acirc;n giải 48 MP, khẩu độ f/1.9, hỗ trợ chống rung OIS, PDAF.</li>\r\n	<li>Camera g&oacute;c si&ecirc;u rộng: Độ ph&acirc;n giải 12 MP, khẩu độ f/2.2, PDAF.</li>\r\n	<li>Camera selfie: Độ ph&acirc;n giải 12 MP, khẩu độ f/2.0.</li>\r\n</ul>\r\n\r\n<p>Sony Xperia 5 V sở hữu th&ocirc;ng số camera chất lượng. Nguồn: GSMArena.</p>\r\n\r\n<p><strong>- Ảnh chụp trong điều kiện đủ s&aacute;ng:</strong></p>\r\n\r\n<p>Trong điều kiện đủ s&aacute;ng, c&aacute;c mẫu chụp bởi camera ch&iacute;nh của Xperia đều c&oacute; sự t&aacute;i hiện chi tiết tốt v&agrave; mật độ nhiễu hạt rất thấp. Tổng thể của ảnh c&oacute; sự&nbsp;c&acirc;n bằng trắng v&agrave; hiển thị m&agrave;u sắc ch&iacute;nh x&aacute;c c&ugrave;ng độ tương phản cao, đồng thời dải dynamic được xử l&yacute; tốt gi&uacute;p c&aacute;c chi tiết phức tạp như t&aacute;n l&aacute; hoặc chi tiết ngẫu nhi&ecirc;n tr&ecirc;n khu&ocirc;n mặt người, bề mặt vật thể trở n&ecirc;n nổi bật.</p>\r\n\r\n<p>Ảnh chụp bởi camera ch&iacute;nh của Xperia 5 V trong điều kiện đủ s&aacute;ng. Nguồn: GSMArena.</p>\r\n\r\n<p>Xperia 5 V được t&iacute;ch hợp sẵn ph&iacute;m tắt 2x, cho ph&eacute;p người d&ugrave;ng c&oacute; thể chụp ảnh thu ph&oacute;ng ở độ ph&acirc;n giải 12 MP. Ở chế độ n&agrave;y, ảnh chụp được cung cấp rất nhiều chi tiết c&oacute; độ ph&acirc;n giải cao, m&agrave;u sắc v&agrave; độ tương phản được xử l&yacute; rất tự nhi&ecirc;n.&nbsp;Mặc d&ugrave; mật độ chi tiết tr&ecirc;n mỗi pixel kh&ocirc;ng thể ngang bằng với đầu ra mức thu ph&oacute;ng 1x ti&ecirc;u chuẩn, nhưng nh&igrave;n chung vẫn ổn định như việc chụp thu ph&oacute;ng tr&ecirc;n camera tele chuy&ecirc;n dụng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img data=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/3-020923-200020.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/3-020923-200020.jpg\" /><img data=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/3-1-020923-200021.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/3-1-020923-200021.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>So s&aacute;nh ảnh chụp ti&ecirc;u chuẩn v&agrave; thu ph&oacute;ng 2x bởi camera ch&iacute;nh của Xperia 5 V trong điều kiện đủ s&aacute;ng. Nguồn: GSMArena.</p>\r\n\r\n<p>Ở khả năng chụp ch&acirc;n dung,&nbsp;Sony thực hiện một v&agrave;i cải tiến tr&ecirc;n Xperia 5 V để n&acirc;ng cao trải nghiệm chụp với t&iacute;nh năng n&agrave;y.&nbsp;C&aacute;c bức ch&acirc;n dung ti&ecirc;u chuẩn được Xperia 5 V được thể hiện ấn tượng, chủ thể được t&aacute;ch khỏi hậu cảnh một c&aacute;ch tự nhi&ecirc;n, sắc n&eacute;t, đầy m&agrave;u sắc v&agrave; độ phơi s&aacute;ng tốt. Đặc biệt, hiện tượng nhiễu hạt được xử l&yacute; triệt để&nbsp;v&agrave; dải dynamic rộng gi&uacute;p thể hiện nổi bật c&aacute;c chi tiết tr&ecirc;n chủ thể.</p>\r\n\r\n<p><img alt=\"Ảnh chụp chân dung từ camera chính của Xperia 5 V\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/4-020923-203511-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/4-020923-203511-800-resize.jpg\" title=\"Ảnh chụp chân dung từ camera chính của Xperia 5 V\" /></p>\r\n\r\n<p>Ảnh chụp ch&acirc;n dung từ camera ch&iacute;nh của Xperia 5 V. Nguồn: GSMArena.</p>\r\n\r\n<p>Khi chụp ch&acirc;n dung bằng Xperia 5 V ở mức thu ph&oacute;ng 2x th&igrave; ảnh chụp vẫn cho chất lượng tốt. Tuy nhi&ecirc;n, ch&uacute;ng bị giảm một ch&uacute;t về mật độ chi tiết v&agrave; độ sắc n&eacute;t tổng thể, nhưng ch&uacute;ng vẫn gần như tuyệt vời như những ảnh được chụp bằng chế độ ch&acirc;n dung ti&ecirc;u chuẩn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img data=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/5-020923-205120.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/5-020923-205120.jpg\" /><img data=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/5-1-020923-205121.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/5-1-020923-205121.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>So s&aacute;nh ảnh chụp ch&acirc;n dung ti&ecirc;u chuẩn v&agrave; thu ph&oacute;ng 2x bởi camera ch&iacute;nh của&nbsp;Xperia 5 V. Nguồn: GSMArena.</p>\r\n\r\n<p>Đến với khả năng chụp ảnh selfie, nhờ v&agrave;o việc sở hữu camera trước c&oacute; độ ph&acirc;n giải 12 MP c&ugrave;ng một số cải tiến về khả năng xử l&yacute; ảnh, Xperia 5 V ho&agrave;n to&agrave;n c&oacute; thể đ&aacute;p ứng những th&agrave;nh phẩm c&oacute; chi tiết sắc n&eacute;t, dải động rộng, độ tương phản tốt v&agrave; m&agrave;u sắc đầy tự nhi&ecirc;n.</p>\r\n\r\n<p><img alt=\"Ảnh selfie được chụp bởi camera selfie 12 MP của Xperia 5 V\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/6-020923-210701-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/6-020923-210701-800-resize.jpg\" title=\"Ảnh selfie được chụp bởi camera selfie 12 MP của Xperia 5 V\" /></p>\r\n\r\n<p>Ảnh selfie được chụp bởi camera selfie 12 MP của Xperia 5 V. Nguồn: GSMArena.</p>\r\n\r\n<p><strong>- Ảnh chụp trong điều kiện thiếu s&aacute;ng:</strong></p>\r\n\r\n<p>Ngo&agrave;i việc cải tiến th&ocirc;ng số phần cứng, Xperia 5 V c&ograve;n được n&acirc;ng cấp ấn tượng về khả năng chụp đ&ecirc;m. Ở camera ch&iacute;nh, khi chụp mặc định th&igrave; hệ thống sẽ tự động k&iacute;ch hoạt chế độ chụp ban đ&ecirc;m Night View. Chế độ n&agrave;y c&oacute; thể cho những ảnh chụp cực kỳ chi tiết, độ b&atilde;o h&ograve;a m&agrave;u sắc vượt trội v&agrave; đặc biệt l&agrave; hiện tượng nhiễu hạt được xử l&yacute; tốt n&ecirc;n hầu như kh&ocirc;ng xuất hiện.</p>\r\n\r\n<p><img alt=\"Ảnh chụp bởi camera chính của Xperia 5 V trong điều kiện thiếu sáng\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/1-020923-214307-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/1-020923-214307-800-resize.jpg\" title=\"Ảnh chụp bởi camera chính của Xperia 5 V trong điều kiện thiếu sáng\" /></p>\r\n\r\n<p>Ảnh chụp bởi camera ch&iacute;nh của Xperia 5 V trong điều kiện thiếu s&aacute;ng. Nguồn: GSMArena.</p>\r\n\r\n<p><img alt=\"Ảnh chụp bởi camera chính của Xperia 5 V trong điều kiện thiếu sáng\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/2-020923-214920-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/2-020923-214920-800-resize.jpg\" title=\"Ảnh chụp bởi camera chính của Xperia 5 V trong điều kiện thiếu sáng\" /></p>\r\n\r\n<p>Ảnh chụp bởi camera ch&iacute;nh của Xperia 5 V trong điều kiện thiếu s&aacute;ng. Nguồn: GSMArena.</p>\r\n\r\n<p>Nếu so s&aacute;nh chất lượng ảnh chụp thường v&agrave; ảnh được xử l&yacute; bởi chế độ chụp đ&ecirc;m, tuy kh&ocirc;ng c&oacute; nhiều sự kh&aacute;c biệt về mật độ chi tiết nhưng chế độ chụp đ&ecirc;m Night View lại l&agrave;m cho th&agrave;nh phẩm trở n&ecirc;n tự nhi&ecirc;n hơn nhờ việc tăng độ phơi s&aacute;ng v&agrave; độ tương phản.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img data=\"https://fdn.gsmarena.com/imgroot/reviews/23/sony-xperia-5-v/camera/gsmarena_126.jpg\" src=\"https://fdn.gsmarena.com/imgroot/reviews/23/sony-xperia-5-v/camera/gsmarena_126.jpg\" /><img data=\"https://fdn.gsmarena.com/imgroot/reviews/23/sony-xperia-5-v/camera/gsmarena_113.jpg\" src=\"https://fdn.gsmarena.com/imgroot/reviews/23/sony-xperia-5-v/camera/gsmarena_113.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>So s&aacute;nh ảnh chụp ti&ecirc;u chuẩn v&agrave; ảnh chụp được xử l&yacute; bởi chế độ chụp đ&ecirc;m Night View tr&ecirc;n Xperia 5 V. Nguồn: GSMArena.</p>\r\n\r\n<p>Những bức ảnh thu ph&oacute;ng 2x chụp bằng chế độ Night View tự động rất xuất sắc v&agrave; được t&aacute;c giả nh&acirc;n định tốt hơn so với c&aacute;c mẫu chụp từ camera tele của thế hệ tiền nhiệm Xperia 5 IV. Ảnh chụp c&oacute; độ phơi s&aacute;ng tốt, chi tiết được thể hiện đầy đủ, độ nhiễu thấp v&agrave; m&agrave;u sắc c&oacute; sự b&atilde;o h&ograve;a. Tuy chất lượng tr&ecirc;n chi tiết tr&ecirc;n mỗi pixel ảnh kh&oacute; c&oacute; thể s&aacute;nh ngang bằng những bức ảnh chụp đ&ecirc;m ở mức thu ph&oacute;ng ti&ecirc;u chuẩn nhưng vẫn đủ tốt để sử dụng cho nhiều mục đ&iacute;ch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img data=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/4-020923-221431.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/4-020923-221431.jpg\" /><img data=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/4-1-020923-221433.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/4-1-020923-221433.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>So s&aacute;nh ảnh chụp ti&ecirc;u chuẩn v&agrave; ảnh chụp 2x được xử l&yacute; bởi chế độ chụp đ&ecirc;m Night View tr&ecirc;n Xperia 5 V. Nguồn: GSMArena.</p>\r\n\r\n<p>Đến với camera si&ecirc;u rộng của Xperia 5 V, ống k&iacute;nh n&agrave;y vẫn cho những hiệu quả ưu việt khi chụp trong m&ocirc;i trường thiếu &aacute;nh s&aacute;ng. Cụ thể, c&aacute;c th&agrave;nh phẩm được chụp c&oacute; độ phơi s&aacute;ng tốt, chi tiết thể hiện sắc n&eacute;t với độ nhiễu thấp, m&agrave;u sắc ch&acirc;n thực v&agrave; c&aacute;c điểm bị ch&aacute;y s&aacute;ng được kh&ocirc;i phục ở những nơi quan trọng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img data=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/1-020923-224043.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/1-020923-224043.jpg\" /><img data=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/1-1-020923-224045.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/1-1-020923-224045.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>So s&aacute;nh ảnh chụp ti&ecirc;u chuẩn v&agrave; ảnh chụp si&ecirc;u rộng được xử l&yacute; bởi chế độ chụp đ&ecirc;m Night View tr&ecirc;n Xperia 5 V. Nguồn: GSMArena.</p>\r\n\r\n<p>Khi kh&ocirc;ng sử dụng chế độ chụp đ&ecirc;m Night View, ảnh chụp bởi camera si&ecirc;u rộng của Xperia 5 V&nbsp;tr&ocirc;ng ch&acirc;n thực hơn, thậm ch&iacute; sắc n&eacute;t hơn với độ nhiễu thấp v&agrave; độ tương phản cao.</p>\r\n\r\n<p><img alt=\"Ảnh chụp bằng camera siêu rộng của Xperia 5 V khi không dùng Night View\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/6-020923-224314-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/6-020923-224314-800-resize.jpg\" title=\"Ảnh chụp bằng camera siêu rộng của Xperia 5 V khi không dùng Night View\" /></p>\r\n\r\n<p>Ảnh chụp bằng camera si&ecirc;u rộng của Xperia 5 V khi kh&ocirc;ng d&ugrave;ng Night View. Nguồn: GSMArena.</p>\r\n\r\n<h3><strong>Xperia 5 V hiệu năng mạnh mẽ với chip Snapdragon 8 Gen 2</strong></h3>\r\n\r\n<p>Trước khi đi v&agrave;o s&acirc;u hơn, c&ugrave;ng m&igrave;nh điểm qua một v&agrave;i th&ocirc;ng số cấu h&igrave;nh của Xperia 5 V c&aacute;c bạn nh&eacute;!</p>\r\n\r\n<ul>\r\n	<li>M&agrave;n h&igrave;nh: K&iacute;ch thước 6.1 inch, tấm nền OLED, độ ph&acirc;n giải Full HD+ (1.080 x 2520 pixels), tần số qu&eacute;t 120 Hz.</li>\r\n	<li>CPU: Snapdragon 8 Gen 2.</li>\r\n	<li>RAM: 8 GB.</li>\r\n	<li>Bộ nhớ trong 128 GB (hỗ trợ mở rộng qua thẻ nhớ l&ecirc;n đến 1 TB).</li>\r\n	<li>Camera sau: 48 MP + 12 MP</li>\r\n	<li>Camera trước: 12 MP.</li>\r\n	<li>Dung lượng pin: 5.000 mAh, hỗ trợ sạc nhanh c&oacute; d&acirc;y 30 W, sạc nhanh kh&ocirc;ng d&acirc;y 15 W.</li>\r\n	<li>Hệ điều h&agrave;nh: Android 13</li>\r\n</ul>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"http://www.thegioididong.com/tin-tuc/tong-hop-chip-snapdragon-nam-2021-1339285\" target=\"_blank\" title=\"Tìm hiểu về vi xử lý Snapdragon 8 Gen 2 cùng nhiều con chip khác của Qualcomm\" type=\"Tìm hiểu về vi xử lý Snapdragon 8 Gen 2 cùng nhiều con chip khác của Qualcomm\">T&igrave;m hiểu về vi xử l&yacute; Snapdragon 8 Gen 2 c&ugrave;ng nhiều con chip kh&aacute;c của Qualcomm</a></p>\r\n\r\n<p><img alt=\"Sony Xperia 5 V cấu hình nổi bật với vi xử lý Snapdragon 8 Gen 2. Nguồn: GSMArena.\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545386/xperia5vcauhinh-010923-154759-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545386/xperia5vcauhinh-010923-154759-800-resize.jpg\" title=\"Sony Xperia 5 V cấu hình nổi bật với vi xử lý Snapdragon 8 Gen 2. Nguồn: GSMArena.\" /></p>\r\n\r\n<p>Sony Xperia 5 V cấu h&igrave;nh nổi bật với vi xử l&yacute; Snapdragon 8 Gen 2. Nguồn: GSMArena.</p>\r\n\r\n<p>Về mặt hiệu năng, bản th&acirc;n Xperia 5 V l&agrave; mẫu điện thoại nằm trong ph&acirc;n kh&uacute;c flagship n&ecirc;n việc m&aacute;y sở hữu vi xử l&yacute; mạnh mẽ như Snapdragon 8 Gen 2 l&agrave; điều dễ hiểu.&nbsp;D&agrave;nh cho những bạn chưa biết, Snapdragon 8 Gen 2 l&agrave; một vi xử l&yacute; h&agrave;ng đầu của Qualcomm được sản xuất tr&ecirc;n tiến tr&igrave;nh 4 nm của TSMC v&agrave; sở hữu 8 nh&acirc;n b&ecirc;n trong, đi k&egrave;m với đ&oacute; c&ograve;n l&agrave; GPU Adreno 740 cho hiệu suất xử&nbsp; l&yacute; h&igrave;nh ảnh mạnh mẽ.</p>\r\n\r\n<p><img alt=\"Xperia 5 V được trang bị chip Snapdragon 8 Gen 2 mạnh mẽ\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/20-010923-215912-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/20-010923-215912-800-resize.jpg\" title=\"Xperia 5 V được trang bị chip Snapdragon 8 Gen 2 mạnh mẽ\" /></p>\r\n\r\n<p>Xperia 5 V được trang bị chip Snapdragon 8 Gen 2 mạnh mẽ. Nguồn: GSMArena.</p>\r\n\r\n<p>Ch&iacute;nh v&igrave; thế, đối với c&aacute;c t&aacute;c vụ từ nhẹ như lướt web, xem video,... cho đến nặng hơn như chiến game c&oacute; đồ họa cao hoặc chỉnh sửa video cũng kh&ocirc;ng l&agrave;m kh&oacute; được&nbsp;Sony Xperia 5 V. Đồng thời, thiết bị cũng được trang bị mức RAM 8 GB v&agrave; bộ nhớ trong 128 GB nhằm hỗ trợ cho Snapdragon 8 Gen 2 hoạt động được mượt m&agrave; v&agrave; truy xuất dữ liệu nhanh ch&oacute;ng. Tuy nhi&ecirc;n, để c&oacute; c&aacute;i nh&igrave;n cụ thể hơn th&igrave; mời c&aacute;c bạn c&ugrave;ng m&igrave;nh điểm qua những b&agrave;i test hiệu năng do trang GSMArena thực hiện ngay dưới đ&acirc;y nh&eacute;!</p>\r\n\r\n<p><img alt=\"Xperia 5 V cùng Snapdragon 8 Gen 2 có thể đáp ứng tốt mọi tác vụ thường nhật\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/22-010923-215111-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/22-010923-215111-800-resize.jpg\" title=\"Xperia 5 V cùng Snapdragon 8 Gen 2 có thể đáp ứng tốt mọi tác vụ thường nhật\" /></p>\r\n\r\n<p>Xperia 5 V c&ugrave;ng Snapdragon 8 Gen 2 c&oacute; thể đ&aacute;p ứng tốt mọi t&aacute;c vụ thường nhật. Nguồn: GSMArena.</p>\r\n\r\n<p>Đầu ti&ecirc;n l&agrave; b&agrave;i test với ứng dụng GeekBench 6, chip Snapdragon 8 Gen 2 tr&ecirc;n Xperia 5 V đ&atilde; thể hiện rất tốt khi cho ra 1.453 điểm/4.546 điểm trong b&agrave;i kiểm tra hiệu năng đơn nh&acirc;n/đa nh&acirc;n. Tuy nhi&ecirc;n khi so với c&aacute;c đối thủ kh&aacute;c c&ugrave;ng sử dụng Snapdragon 8 Gen 2 như ASUS Zenfone 10 hoặc Samsung Galaxy S23, điểm số m&agrave;&nbsp;Sony Xperia 5 V đạt được lại kh&ocirc;ng qu&aacute; ấn tượng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img data=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/5-010923-204949.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/5-010923-204949.jpg\" /><img data=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/6-010923-204952.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/6-010923-204952.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Điểm hiệu năng đơn nh&acirc;n/đa nh&acirc;n của Xperia 5 V được chấm bởi Geekbench 6. Nguồn: GSMArena.</p>\r\n\r\n<p>C&ograve;n đối với b&agrave;i test với ứng dụng Antutu v10, Xperia 5 V vẫn cho ra kết quả tốt với 1.117.716 điểm nhưng nếu so với v&agrave;i thiết bị cũng được trang bị mẫu chip tương tự th&igrave; đại diện đến từ Sony tiếp tục kh&ocirc;ng thực sự nổi bật.</p>\r\n\r\n<p><img alt=\"Kết quả kiểm tra hiệu năng Xperia 5 V bằng phần mềm Antutu v10\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/7-010923-205717-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/7-010923-205717-800-resize.jpg\" title=\"Kết quả kiểm tra hiệu năng Xperia 5 V bằng phần mềm Antutu v10\" /></p>\r\n\r\n<p>Kết quả kiểm tra hiệu năng Xperia 5 V bằng phần mềm Antutu v10. Nguồn: GSMArena.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, c&aacute;c kết quả tr&ecirc;n chỉ ở mức tương đối v&agrave;&nbsp;đ&acirc;y chỉ mới l&agrave; những b&agrave;i test tổng quan v&agrave; vẫn c&ograve;n nhẹ nh&agrave;ng, do đ&oacute; t&aacute;c giả đ&atilde; sử dụng phần mềm CPU Throttle vốn nặng hơn để kiểm tra sự ổn định hiệu năng của Snapdragon 8 Gen 2 tr&ecirc;n Xperia 5 V. C&oacute; thể thấy rằng sau khoảng 60 ph&uacute;t hoạt động nặng, Xperia 5 V sụt giảm hiệu năng rất nhiều v&agrave; chỉ đ&aacute;p ứng khoảng 40% so với ban đầu; đồng thời thiết bị cũng c&oacute; n&oacute;ng l&ecirc;n ở khu vực gần camera.</p>\r\n\r\n<p><img alt=\"Kết quả kiểm tra độ ổn định hiệu năng với 3DMark Wild Life Extreme và CPU Throttlle\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/hieunangxperia5v-010923-212823-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/hieunangxperia5v-010923-212823-800-resize.jpg\" title=\"Kết quả kiểm tra độ ổn định hiệu năng với 3DMark Wild Life Extreme và CPU Throttlle\" /></p>\r\n\r\n<p>Kết quả kiểm tra độ ổn định hiệu năng với 3DMark Wild Life Extreme v&agrave; CPU Throttle. Nguồn: GSMArena.</p>\r\n\r\n<p>Điều đ&oacute; cũng được kiểm chứng r&otilde; r&agrave;ng hơn khi t&aacute;c giả sử dụng Xperia 5 V với phần mềm 3DMark Wild Life Extreme, hiệu năng cao nhất v&agrave; thấp nhất m&agrave; m&aacute;y đạt được c&oacute; sự c&aacute;ch biệt qu&aacute; lớn v&agrave; độ ổn định (Stability) cũng chỉ ở mức 34.8% m&agrave; th&ocirc;i. Tuy vậy, khi sử dụng Xperia 5 V để chơi game nặng trong khoảng 30 ph&uacute;t, t&aacute;c giả lại thấy rằng thiết bị hoạt động tương đối ổn định v&agrave; kh&ocirc;ng thực sự qu&aacute; n&oacute;ng.</p>\r\n\r\n<h3><strong>Xperia 5 V pin 5.000 mAh cho thời lượng sử dụng l&acirc;u d&agrave;i</strong></h3>\r\n\r\n<p>Giống với thế hệ tiền nhiệm, Sony Xperia 5 V được cung cấp năng lượng bởi vi&ecirc;n pin 5.000 mAh, đ&acirc;y cũng l&agrave; mức dung lượng lớn đang dần được nhiều nh&agrave; sản xuất smartphone sử dụng tr&ecirc;n mọi ph&acirc;n kh&uacute;c sản phẩm. Vậy vi&ecirc;n pin 5.000 mAh n&agrave;y c&oacute; thể gi&uacute;p Xperia 5 V hoạt động trong bao l&acirc;u?</p>\r\n\r\n<p>Xperia 5 V được trang bị pin 5.000 mAh. Nguồn: GSMArena.</p>\r\n\r\n<p>Qua tiến tr&igrave;nh đ&aacute;nh gi&aacute; của GSMArena, Xperia 5 V đ&aacute;p ứng thời lượng hoạt động d&agrave;i l&acirc;u với 39 giờ 53 ph&uacute;t gọi thoại, 18 giờ 19 ph&uacute;t lướt web, 21 giờ 59 ph&uacute;t ph&aacute;t video v&agrave; l&ecirc;n đến 130 giờ khi ở chế độ chờ. Điều n&agrave;y cho thấy được Xperia 5 V đ&atilde; được tối ưu tốt&nbsp;về&nbsp;mức sử dụng điện năng của vi xử l&yacute; nhằm cải thiện thời lượng trải nghiệm cho người d&ugrave;ng.</p>\r\n\r\n<p>Kết quả kiểm tra thời lượng sử dụng Xperia 5 V. Nguồn: GSMArena.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, Xperia 5 V c&ograve;n được t&iacute;ch hợp t&iacute;nh năng sạc nhanh với c&ocirc;ng suất 30 W nhưng m&aacute;y kh&ocirc;ng c&oacute; c&aacute;c trang bị đi k&egrave;m như củ sạc hay c&aacute;p sạc b&ecirc;n trong hộp. Do đ&oacute;, để c&oacute; thể tận dụng được t&iacute;nh năng sạc nhanh 30 W th&igrave; bạn cần phải sở hữu th&ecirc;m một bộ sạc với mức c&ocirc;ng suất tối thiểu l&agrave; 30 W. Để c&oacute; thể thực hiện b&agrave;i tiến tr&igrave;nh test tốc độ sạc nhanh của Xperia 5 V, t&aacute;c giả đ&atilde; sử dụng bộ sạc XQZ-UC1 PD của ch&iacute;nh Sony với cổng USB Type-C 30 W chuy&ecirc;n dụng v&agrave; cho ra kết quả như sau:</p>\r\n\r\n<p><img alt=\"Thời gian sạc đầy pin từ mức 0% trên Xperia 5 V\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/2-010923-230032-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545388/2-010923-230032-800-resize.jpg\" title=\"Thời gian sạc đầy pin từ mức 0% trên Xperia 5 V\" /></p>\r\n\r\n<p>Thời gian sạc đầy pin từ mức 0% tr&ecirc;n Xperia 5 V. Nguồn: GSMArena.</p>\r\n\r\n<p>Nh&igrave;n chung, nhờ việc được trang bị sạc nhanh c&ocirc;ng suất 30 W n&ecirc;n Xperia 5 V c&oacute; thể nạp đầy pin từ&nbsp;0% - 100% trong v&ograve;ng 1 tiếng 33 ph&uacute;t, một kết quả tương đối tốt so với thiết bị sở hữu sạc nhanh 30 W. Trong trường hợp m&aacute;y hết pin nhưng bạn cần sử dụng gấp th&igrave; chỉ với 15 ph&uacute;t sạc, m&aacute;y đ&atilde; c&oacute; 27% pin v&agrave; con số c&oacute; thể l&ecirc;n đến 50% khi ch&uacute;ng ta sạc m&aacute;y trong v&ograve;ng 30 ph&uacute;t.</p>\r\n\r\n<h3><strong>Tổng kết</strong></h3>\r\n\r\n<p>Nh&igrave;n chung, Xperia 5 V l&agrave; phi&ecirc;n bản n&acirc;ng cấp nhẹ so với thế hệ trước về mặt thiết kế, hiệu năng cũng như camera (theo nhận định từ trang GSMArena). Tất nhi&ecirc;n, mẫu flagship nhỏ gọn mới của Sony vẫn l&agrave; một trong những chiếc điện thoại đ&aacute;ng sở hữu ở thời điểm hiện tại. Gi&aacute; Xperia 5 V&nbsp;tại thị trường ch&acirc;u &Acirc;u l&agrave; 999 Euro (khoảng 26.1 triệu đồng) cho phi&ecirc;n bản RAM 8 GB, bộ nhớ trong 128 GB.</p>\r\n\r\n<p><img alt=\"Xperia 5 V là mẫu flagship nhỏ gọn đáng sở hữu ở thời điểm hiện tại. Nguồn: GSMArena.\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545386/xperia5vcogimoi-010923-170550-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545386/xperia5vcogimoi-010923-170550-800-resize.jpg\" title=\"Xperia 5 V là mẫu flagship nhỏ gọn đáng sở hữu ở thời điểm hiện tại. Nguồn: GSMArena.\" /></p>\r\n\r\n<p>Xperia 5 V l&agrave; mẫu flagship nhỏ gọn đ&aacute;ng sở hữu ở thời điểm hiện tại. Nguồn: GSMArena.</p>\r\n\r\n<p>Vậy c&aacute;c bạn nghĩ sao về Sony Xperia 5 V? H&atilde;y để lại b&igrave;nh luận b&ecirc;n dưới cho ch&uacute;ng m&igrave;nh được biết nha! Cảm ơn c&aacute;c bạn đ&atilde; quan t&acirc;m v&agrave; theo d&otilde;i b&agrave;i viết của m&igrave;nh.</p>\r\n');
INSERT INTO `tb_posts` (`post_id`, `post_title`, `post_img`, `slug`, `status`, `created_date`, `creator`, `post_content`) VALUES
(9, 'Đánh giá Acer Aspire 3 A314 36M 391A và A315 510P 32EF: Thiết kế tối giản, hiệu năng tốt, pin dùng lâu', 'list_post8.jpg', 'Đánh giá Acer Aspire 3 A314 36M 391A và A315 510P 32EF', 'Đã đăng', '2023-10-09 17:23:03', 'vuhonglinh123', '<h3><strong>Ngoại h&igrave;nh Acer Aspire 3 A314 v&agrave; A315 theo hướng tối giản, mỏng nhẹ</strong></h3>\r\n\r\n<p>Về mặt tổng thể, Acer Aspire 3 A314 v&agrave; A315 c&oacute; ngoại h&igrave;nh tương đồng nhau khi&nbsp;sở hữu thiết kế hiện đại với th&acirc;n h&igrave;nh mỏng nhẹ, c&aacute;c g&oacute;c cạnh vu&ocirc;ng vắn phối hợp c&ugrave;ng m&agrave;u trắng bạc chủ đạo gi&uacute;p tạo n&ecirc;n một vẻ đẹp thanh lịch v&agrave; tinh tế. Cả hai thiết bị đều được ho&agrave;n thiện từ chất liệu nhựa bền bỉ với mức độ ho&agrave;n thiện tốt, do đ&oacute; trong qu&aacute; tr&igrave;nh trải nghiệm m&igrave;nh kh&ocirc;ng nhận thấy hiện tượng l&uacute;n ph&iacute;m hoặc rung lắc m&agrave;n h&igrave;nh qu&aacute; nhiều.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img data=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-7-310823-230749.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-7-310823-230749.jpg\" /><img data=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-2-310823-230907.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-2-310823-230907.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Acer Aspire 3 A314 v&agrave; A315 c&oacute; kiểu d&aacute;ng tương đồng với c&aacute;c g&oacute;c cạnh vu&ocirc;ng vắn c&ugrave;ng m&agrave;u trắng bạc chủ đạo.</p>\r\n\r\n<p>Ngo&agrave;i ra, Acer Aspire 3 A315 v&agrave; A315 đều c&oacute; c&ugrave;ng số lượng cổng kết nối v&agrave; c&aacute;ch bố tr&iacute; cũng tương đồng. Cụ thể, ở cạnh tr&aacute;i của m&aacute;y ta sẽ bắt gặp cổng sạc, cổng USB Type-C, cổng HDMI v&agrave; cổng USB Type-A 3.2; trong khi đ&oacute; cạnh b&ecirc;n phải lại c&oacute; lỗ kh&oacute;a Kenshington, cổng USB Type-A 3.2 v&agrave; jack cắm tai nghe 3.5 mm.</p>\r\n\r\n<p><img alt=\"Cạnh trái của Acer Aspire 3 A314 và A315\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-14-310823-231116-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-14-310823-231116-800-resize.jpg\" title=\"Cạnh trái của Acer Aspire 3 A314 và A315\" /></p>\r\n\r\n<p>Cạnh tr&aacute;i của Acer Aspire 3 A314 (tr&ecirc;n) v&agrave; A315 (dưới).</p>\r\n\r\n<p><img alt=\"Cạnh phải của Acer Aspire 3 A314 và A315\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-13-310823-231108-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-13-310823-231108-800-resize.jpg\" title=\"Cạnh phải của Acer Aspire 3 A314 và A315\" /></p>\r\n\r\n<p>Cạnh phải của Acer Aspire 3 A314 (tr&ecirc;n) v&agrave; A315 (dưới).</p>\r\n\r\n<p>Mặc d&ugrave; c&oacute; nhiều điểm tương đồng nhưng Acer Aspire 3 A314 v&agrave; A315 vẫn c&oacute; sự kh&aacute;c biệt. Đầu ti&ecirc;n l&agrave; về khối lượng v&agrave; k&iacute;ch thước, trong khi Acer Aspire 3 A314 c&oacute; c&acirc;n nặng chỉ 1.4 kg th&igrave; A315 lại nặng đến 1.7 kg cũng như c&oacute; k&iacute;ch cỡ to lớn hơn. Tuy vậy, nhờ v&agrave;o việc được thiết kế theo xu hướng tinh giản, gọn nhẹ n&ecirc;n cả hai thiết bị vẫn gi&uacute;p m&igrave;nh dễ d&agrave;ng cho v&agrave;o trong balo để mang đi thuận lợi.</p>\r\n\r\n<p><img alt=\"Acer Aspire 3 A315 (phải) có kích thước lớn hơn và nặng hơn so với phiên bản A314\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-4-310823-231227-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-4-310823-231227-800-resize.jpg\" title=\"Acer Aspire 3 A315 (phải) có kích thước lớn hơn và nặng hơn so với phiên bản A314\" /></p>\r\n\r\n<p>Acer Aspire 3 A314 (tr&aacute;i) c&oacute; k&iacute;ch thước nhỏ v&agrave; nhẹ hơn so với phi&ecirc;n bản A315 (phải).</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, Acer Aspire 3 A314 được nh&agrave; sản xuất trang bị m&agrave;n h&igrave;nh IPS c&oacute; k&iacute;ch thước 14 inch v&agrave; độ ph&acirc;n giải Full HD (1.920 x 1.080 pixel), đủ để mang đến cho người d&ugrave;ng kh&ocirc;ng gian l&yacute; tưởng để l&agrave;m việc hoặc thực hiện c&aacute;c t&aacute;c vụ giải tr&iacute; đa phương tiện. C&ograve;n ở Acer Aspire 3 A315, thiết bị lại sở hữu m&agrave;n h&igrave;nh với th&ocirc;ng số tương tự nhưng lại c&oacute; k&iacute;ch thước 15.6 inch, gi&uacute;p mang đến một kh&ocirc;ng gian hiển thị rộng r&atilde;i để người d&ugrave;ng c&oacute; thể thực hiện linh hoạt nhiều t&aacute;c vụ c&ugrave;ng một l&uacute;c hoặc trải nghiệm giải tr&iacute; to&agrave;n diện hơn.</p>\r\n\r\n<p>Acer Aspire 3 A314 (tr&aacute;i) sở hữu m&agrave;n h&igrave;nh 14 inch v&agrave; Acer Aspire 3 A315 (phải) được trang bị m&agrave;n h&igrave;nh 15.6 inch.</p>\r\n\r\n<p>Mở ra b&ecirc;n trong, Acer Aspire 3 A314 được trang bị hệ thống b&agrave;n ph&iacute;m 67% gọn g&agrave;ng rất dễ d&agrave;ng l&agrave;m quen. C&aacute;c ph&iacute;m c&oacute; k&iacute;ch thước lớn, khoảng c&aacute;ch hợp l&yacute;, độ nảy tốt v&agrave; đặc biệt l&agrave; c&ograve;n phủ nh&aacute;m n&ecirc;n thiết bị cho m&igrave;nh cảm gi&aacute;c g&otilde; rất th&iacute;ch tay. C&ograve;n ở Acer Aspire 3 A315, hệ thống b&agrave;n ph&iacute;m của m&aacute;y cũng c&oacute; c&aacute;c t&iacute;nh chất tương tự phi&ecirc;n bản 14 inch nhưng lại sử dụng layout full size với b&agrave;n ph&iacute;m số cực tiện dụng, rất th&iacute;ch hợp cho c&aacute;c bạn thường l&agrave;m những c&ocirc;ng việc c&oacute; li&ecirc;n quan đến số liệu.</p>\r\n\r\n<p>B&agrave;n ph&iacute;m của Acer Aspire 3 A314 v&agrave; A315 cho trải nghiệm sử dụng tốt.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, cả hai phi&ecirc;n bản A314 v&agrave; A315 đều kh&ocirc;ng c&oacute; đ&egrave;n nền dưới b&agrave;n ph&iacute;m n&ecirc;n đ&acirc;y ch&iacute;nh l&agrave; điểm trừ ảnh hưởng đến trải nghiệm của m&igrave;nh khi sử dụng m&aacute;y trong những m&ocirc;i trường c&oacute; cường độ s&aacute;ng yếu. B&ugrave; lại, c&aacute;c thiết bị được trang bị b&agrave;n di chuột c&oacute; k&iacute;ch thước vừa phải đủ để m&igrave;nh thực hiện tương t&aacute;c, b&ecirc;n tr&ecirc;n bề mặt được phủ nh&aacute;m gi&uacute;p mang đến cảm gi&aacute;c di chuột thoải m&aacute;i v&agrave; kh&ocirc;ng bị r&iacute;t tay.</p>\r\n\r\n<h3><strong>Acer Aspire 3 A314 v&agrave; A315 hiệu năng tốt với Intel Core i3-N305</strong></h3>\r\n\r\n<p>Trước khi đi v&agrave;o chi tiết, c&ugrave;ng m&igrave;nh điểm qua c&aacute;c th&ocirc;ng số cấu h&igrave;nh của Acer Aspire 3 A314 v&agrave; A315 (phi&ecirc;n bản đang được đ&aacute;nh gi&aacute; trong b&agrave;i viết n&agrave;y).</p>\r\n\r\n<p><strong>- Acer Aspire 3 A314:</strong></p>\r\n\r\n<ul>\r\n	<li>M&agrave;n h&igrave;nh: K&iacute;ch thước 14 inch, tấm nền IPS, độ ph&acirc;n giải&nbsp;Full HD (1920 x 1080 pixels).</li>\r\n	<li>CPU: Intel Core i3-N305.</li>\r\n	<li>RAM: 8 GB LPDDR5.</li>\r\n	<li>Ổ cứng: 512 GB SSD NVMe PCIe.</li>\r\n	<li>Card đồ họa: Card t&iacute;ch hợp - Intel Iris Xe Graphics.</li>\r\n	<li>Pin: 40 Wh, sạc 45 W.&nbsp;</li>\r\n	<li>Hệ điều h&agrave;nh: Windows 11.</li>\r\n</ul>\r\n\r\n<p><img alt=\"Thông số cấu hình Acer Aspire 3 A314\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-43-310823-152607-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-43-310823-152607-800-resize.jpg\" title=\"Thông số cấu hình Acer Aspire 3 A314\" /></p>\r\n\r\n<p>Th&ocirc;ng số cấu h&igrave;nh Acer Aspire 3 A314.</p>\r\n\r\n<p><strong>- Acer Aspire 3 A315:</strong></p>\r\n\r\n<ul>\r\n	<li>M&agrave;n h&igrave;nh: K&iacute;ch thước 15.6 inch,&nbsp;tấm nền IPS, độ ph&acirc;n giải&nbsp;Full HD (1920 x 1080 pixels).</li>\r\n	<li>CPU: Intel Core i3-N305.</li>\r\n	<li>RAM: 8 GB LPDDR5.</li>\r\n	<li>Ổ cứng: 256 GB SSD NVMe PCIe.</li>\r\n	<li>Card đồ họa: Card t&iacute;ch hợp - Intel Iris Xe Graphics.</li>\r\n	<li>Pin: 40 Wh, sạc 45 W.&nbsp;</li>\r\n	<li>Hệ điều h&agrave;nh: Windows 11.</li>\r\n</ul>\r\n\r\n<p><img alt=\"Thông số cấu hình Acer Aspire 3 A315\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-42-310823-152521-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-42-310823-152521-800-resize.jpg\" title=\"Thông số cấu hình Acer Aspire 3 A315\" /></p>\r\n\r\n<p>Th&ocirc;ng số cấu h&igrave;nh Acer Aspire 3 A315.</p>\r\n\r\n<p>Về mặt hiệu năng, Acer Aspire 3 A314 v&agrave; A315 c&oacute; th&ocirc;ng số gần như tương đồng khi đều được trang bị bộ vi xử l&yacute; Intel Core i3-N305. D&agrave;nh cho c&aacute;c bạn chưa biết, đ&acirc;y l&agrave; mẫu chip kế thừa từ d&ograve;ng vi xử l&yacute; Celeron N được cấu th&agrave;nh từ 8 nh&acirc;n tiết kiệm điện với xung nhịp c&oacute; thể đạt đến 3.80 GHz. Do đ&oacute;, Intel Core i3-N305 thường xuất hiện tr&ecirc;n c&aacute;c mẫu laptop gi&aacute; rẻ như&nbsp;Acer Aspire 3 A314 v&agrave; A315,&nbsp;cung cấp hiệu năng để xử l&yacute; tốt c&aacute;c t&aacute;c vụ văn ph&ograve;ng, giải tr&iacute; đơn giản.</p>\r\n\r\n<p><img alt=\"Acer Aspire 3 A314 và A315 đều được trang bị chip Intel Core i3-N305\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-47-310823-151758-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-47-310823-151758-800-resize.jpg\" title=\"Acer Aspire 3 A314 và A315 đều được trang bị chip Intel Core i3-N305\" /></p>\r\n\r\n<p>Acer Aspire 3 A314 v&agrave; A315 đều được trang bị chip Intel Core i3-N305.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, Acer Aspire 3 A314 v&agrave; A315 sử dụng card đồ họa Intel UHD Graphics được t&iacute;ch hợp ngay tr&ecirc;n vi xử l&yacute;, tuy hiệu năng kh&ocirc;ng qu&aacute; mạnh nhưng vẫn đủ để&nbsp;gi&uacute;p m&aacute;y c&oacute; khả năng xử l&yacute; đồ họa tốt, phục vụ tốt c&aacute;c nhu cầu nhẹ nh&agrave;ng như chỉnh sửa h&igrave;nh ảnh đơn giản, xem phim hoặc chơi c&aacute;c tựa game ở mức thiết lập thấp.</p>\r\n\r\n<p>Acer Aspire 3 A314 v&agrave; A315 sử dụng nh&acirc;n đồ họa Intel UHD Graphics.</p>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;Acer Aspire 3 A314 v&agrave; A315 đều được trang bị dung lượng RAM 8 GB, đủ để người d&ugrave;ng c&oacute; thể sử dụng đa nhiệm hiệu quả đối với c&aacute;c t&aacute;c vụ văn ph&ograve;ng, tuy nhi&ecirc;n m&aacute;y lại hoạt động với chuẩn RAM LPDDR5 t&iacute;ch hợp n&ecirc;n việc n&acirc;ng cấp th&ecirc;m l&agrave; điều gần như kh&ocirc;ng thể. B&ugrave; lại, với ổ cứng SSD 256 GB (Acer Aspire 3 A315) hoặc 512 GB (Acer Aspire 3 A314), người d&ugrave;ng sẽ c&oacute; được kh&ocirc;ng gian rộng lớn để lưu trữ c&aacute;c dữ liệu c&aacute; nh&acirc;n phục vụ c&ocirc;ng việc hoặc giải tr&iacute;.</p>\r\n\r\n<p>Acer Aspire 3 A314 được trang bị SSD 512 GB v&agrave; 256 GB tr&ecirc;n A315, cả hai đều c&oacute; RAM 8 GB.</p>\r\n\r\n<p>Để bạn c&oacute; c&aacute;i nh&igrave;n tổng quan về hiệu năng của chip Intel Core i3-N305 tr&ecirc;n bộ đ&ocirc;i&nbsp;Acer Aspire 3 A314 v&agrave; A315, m&igrave;nh sẽ sử dụng phi&ecirc;n bản Acer Aspire 3 A315 để thực hiện c&aacute;c b&agrave;i chấm điểm hiệu năng bằng c&aacute;c&nbsp;phần mềm như Geekbench 6, Cinebench R23, 3DMark v&agrave; PCMark 10. Kết quả m&igrave;nh thu được như sau:</p>\r\n\r\n<p>- Geekbench 6:</p>\r\n\r\n<ul>\r\n	<li>Đơn nh&acirc;n/đa nh&acirc;n: 1.322 điểm/4.998 điểm.</li>\r\n	<li>Đồ họa OpenCL: 6.314 điểm.</li>\r\n	<li>Đồ họa Vulkan: 7.204 điểm.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img data=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot21-310823-144736.png\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot21-310823-144736.png\" /><img data=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot22-310823-144739.png\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot22-310823-144739.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Điểm hiệu năng Acer Aspire 3 A315 được chấm bởi Geekbench 6.</p>\r\n\r\n<p><img alt=\"Điểm hiệu năng Acer Aspire 3 A315 được chấm bởi Geekbech 6.\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot23-310823-144845-800-resize.png\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot23-310823-144845-800-resize.png\" title=\"Điểm hiệu năng Acer Aspire 3 A315 được chấm bởi Geekbech 6.\" /></p>\r\n\r\n<p>Điểm hiệu năng Acer Aspire 3 A315 được chấm bởi Geekbench 6.</p>\r\n\r\n<p>- Cinebench R23 (Đơn nh&acirc;n/đa nh&acirc;n): 998 điểm/4.739 điểm.</p>\r\n\r\n<p><img alt=\"Điểm hiệu năng Acer Aspire 3 A315 được chấm bởi Cinabench R23\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot26-310823-144919-800-resize.png\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot26-310823-144919-800-resize.png\" title=\"Điểm hiệu năng Acer Aspire 3 A315 được chấm bởi Cinabench R23\" /></p>\r\n\r\n<p>Điểm hiệu năng Acer Aspire 3 A315 được chấm bởi Cinebench R23.</p>\r\n\r\n<p>- PCMark 10: 3.506 điểm.</p>\r\n\r\n<p><img alt=\"Điểm hiệu năng Acer Aspire 3 A315 được chấm bởi PCMark 10\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot24-310823-145158-800-resize.png\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot24-310823-145158-800-resize.png\" title=\"Điểm hiệu năng Acer Aspire 3 A315 được chấm bởi PCMark 10\" /></p>\r\n\r\n<p>Điểm hiệu năng Acer Aspire 3 A315 được chấm bởi PCMark 10.</p>\r\n\r\n<p>- 3DMark Time Spy: 703 điểm.</p>\r\n\r\n<p><img alt=\"Điểm hiệu năng Acer Aspire 3 A315 được chấm bởi 3DMark Time Spy\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot25-310823-145312-800-resize.png\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot25-310823-145312-800-resize.png\" title=\"Điểm hiệu năng Acer Aspire 3 A315 được chấm bởi 3DMark Time Spy\" /></p>\r\n\r\n<p>Điểm hiệu năng Acer Aspire 3 A315 được chấm bởi 3DMark Time Spy.</p>\r\n\r\n<p>Nh&igrave;n chung, điểm hiệu năng m&agrave; Acer Aspire 3 A315 đạt được tương đối tốt so với một v&agrave;i mẫu laptop kh&aacute;c trong ph&acirc;n kh&uacute;c gi&aacute; rẻ. Th&ocirc;ng qua c&aacute;c trải nghiệm thực tế với một v&agrave;i t&aacute;c vụ văn ph&ograve;ng hoặc đồ họa đơn giản, m&igrave;nh cảm thấy thiết bị hoạt động rất trơn tru ngay cả khi m&igrave;nh thường c&oacute; th&oacute;i quen mở nhiều ứng dụng hoặc tab duyệt web c&ugrave;ng một l&uacute;c. Tất nhi&ecirc;n, với việc trang bị c&ugrave;ng một bộ vi xử l&yacute; Intel Core i3-N305 th&igrave; Acer Aspire 3 A314 cũng đạt được những kết quả tương tự.</p>\r\n\r\n<p>Intel Core i3-N305 mang đến cho Acer Aspire 3 A314 v&agrave; A315 hiệu năng tốt.</p>\r\n\r\n<p>B&ecirc;n cạnh việc chấm điểm hiệu năng, m&igrave;nh cũng chơi thử hai tựa game l&agrave; FIFA Online 4 v&agrave; Li&ecirc;n Minh Huyền Thoại tr&ecirc;n Acer Aspire 3 A315. Ở cả hai game n&agrave;y m&igrave;nh đều chỉnh đồ họa l&ecirc;n mức cao nhất v&agrave; sử dụng phần mềm MSI Afterburner để đo số khung h&igrave;nh FPS, nhiệt độ CPU, mức RAM ti&ecirc;u thụ,... cho c&aacute;c bạn c&oacute; một c&aacute;i nh&igrave;n trực quan hơn.</p>\r\n\r\n<p>C&ugrave;ng m&igrave;nh trải nghiệm chiến game tr&ecirc;n Acer Aspire 3 A315 nh&eacute;.</p>\r\n\r\n<p>Đầu ti&ecirc;n l&agrave; tựa game FIFA Online 4, Acer Aspire 3 A315 hoạt động rất ổn với tốc độ khung h&igrave;nh cố định trong khoảng 60 FPS trong suốt một trận đấu, mặc d&ugrave; vẫn c&oacute; hiện tượng giảm FPS khi diễn ra c&aacute;c hiệu ứng chuyển cảnh đột ngột như pha xem lại b&agrave;n thắng, tua chậm,... nhưng kh&ocirc;ng qu&aacute; ảnh hưởng đến trải nghiệm của m&igrave;nh. Về khả năng xử l&yacute; đồ họa th&igrave; c&aacute;c cử chỉ, chuyển động của cầu thủ, chi tiết trong game đều được Acer Aspire 3 A315 thể hiện ch&acirc;n thực, sống động.</p>\r\n\r\n<p>Thiết lập đồ họa game FIFA Online 4 tr&ecirc;n Acer Aspire 3 A315.</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.thegioididong.com/tin-tuc/danh-gia-acer-aspire-3-a314-va-a315-1545168\" /></p>\r\n\r\n<p>Trải nghiệm FIFA Online 4 tr&ecirc;n Acer Aspire 3 A315.</p>\r\n\r\n<p>C&ograve;n ở tựa game MOBA quốc d&acirc;n Li&ecirc;n Minh Huyền Thoại, Acer Aspire 3 A315&nbsp;vẫn thể hiện tốt trong việc xử l&yacute; tr&ograve; chơi n&agrave;y khi m&igrave;nh nhận được một trải nghiệm chơi game mượt m&agrave; với FPS dao động từ 80 - 100 FPS. D&ugrave; mức FPS c&oacute; sự biến động nhưng trải nghiệm của m&igrave;nh l&agrave; rất mượt m&agrave;, th&iacute;ch mắt với c&aacute;c chuyển động trơn tru. Phần đồ họa trong game vẫn được thể hiện tốt với c&aacute;c khung cảnh, chi tiết nh&acirc;n vật v&agrave; hiệu ứng chi&ecirc;u thức được thể hiện m&atilde;n nh&atilde;n.</p>\r\n\r\n<p><img alt=\"Thiết lập đồ họa game Liên Minh Huyền Thoại trên Acer Aspire 3 A315\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/leagueoflegendstmclient8_29_202310_15_52pm-310823-142342-800-resize.png\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/leagueoflegendstmclient8_29_202310_15_52pm-310823-142342-800-resize.png\" title=\"Thiết lập đồ họa game Liên Minh Huyền Thoại trên Acer Aspire 3 A315\" /></p>\r\n\r\n<p>Thiết lập đồ họa game Li&ecirc;n Minh Huyền Thoại tr&ecirc;n Acer Aspire 3 A315.</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.thegioididong.com/tin-tuc/danh-gia-acer-aspire-3-a314-va-a315-1545168\" /></p>\r\n\r\n<p>Trải nghiệm Li&ecirc;n Minh Huyền Thoại tr&ecirc;n Acer Aspire 3 A315.</p>\r\n\r\n<p>C&oacute; thể thấy rằng, tuy l&agrave; một chip xử l&yacute; kh&ocirc;ng qu&aacute; mạnh nhưng hiệu năng m&agrave; Intel Core i3-N305 cung cấp cho Acer Aspire 3 A315 vẫn đủ tốt để người d&ugrave;ng c&oacute; thể giải tr&iacute; với một v&agrave;i tựa game phổ biến. B&ecirc;n cạnh đ&oacute;, m&igrave;nh cũng sử dụng phần mềm Crystal Diskmark để đo tốc độ đọc/ghi của ổ cứng SSD được trang bị b&ecirc;n trong Acer Aspire 3 A315, c&aacute;c kết quả m&igrave;nh thu được như sau:</p>\r\n\r\n<ul>\r\n	<li>Tốc độ đọc: 3.145 MB/s.</li>\r\n	<li>Tốc độ ghi: 1.131 MB/s.</li>\r\n</ul>\r\n\r\n<p><img alt=\"Tốc độ đọc, ghi của ổ cứng SSD trên Acer Aspire 3 A315\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot20-310823-142239.png\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot20-310823-142239.png\" title=\"Tốc độ đọc, ghi của ổ cứng SSD trên Acer Aspire 3 A315\" /></p>\r\n\r\n<p>Tốc độ đọc, ghi của ổ cứng SSD tr&ecirc;n Acer Aspire 3 A315.</p>\r\n\r\n<p>Bản th&acirc;n Acer Aspire 3 A315 cũng như A314 được thiết kế hướng đến nh&oacute;m đối tượng l&agrave;m việc văn ph&ograve;ng n&ecirc;n ngo&agrave;i cấu h&igrave;nh vừa đủ d&ugrave;ng th&igrave; bộ nhớ cũng l&agrave; một yếu tố m&agrave; nh&oacute;m kh&aacute;ch h&agrave;ng n&agrave;y rất quan t&acirc;m. Thiết bị sở hữu ổ cứng lưu trữ lớn kết hợp c&ugrave;ng tốc độ đọc/ghi dữ liệu ấn tượng, do đ&oacute; m&igrave;nh tin chắc c&aacute;c bạn sẽ rất h&agrave;i l&ograve;ng khi việc copy hay di chuyển dữ liệu, tệp tin giữa thiết bị với c&aacute;c ổ cứng b&ecirc;n ngo&agrave;i được thực hiện một c&aacute;ch rất nhanh ch&oacute;ng đấy.</p>\r\n\r\n<h3><strong>Pin Acer Aspire 3 A314 v&agrave; A315 cho thời lượng sử dụng ấn tượng</strong></h3>\r\n\r\n<p>Về thời lượng pin, cả hai thiết bị Acer Aspire 3 A314 v&agrave; A315 đều được trang bị vi&ecirc;n pin c&ocirc;ng suất 40 Wh.&nbsp;Để kiểm chứng vi&ecirc;n pin n&agrave;y c&oacute; thể đ&aacute;p ứng thời lượng sử dụng bao l&acirc;u, m&igrave;nh đ&atilde; sử dụng phần mềm BatteryMon để kiểm tra với 2 trường hợp sử dụng kh&aacute;c nhau. Điều kiện v&agrave; kết quả kiểm tra m&agrave; m&igrave;nh thu được tr&ecirc;n mỗi thiết bị như sau:</p>\r\n\r\n<p><strong>- Acer Aspire 3 A314:</strong></p>\r\n\r\n<p>- Trường hợp 1: D&ugrave;ng c&aacute;c t&aacute;c vụ th&ocirc;ng thường v&agrave; kh&ocirc;ng chơi game:</p>\r\n\r\n<p>Kiểm tra thời lượng pin Acer Aspire 3 A314 với t&aacute;c vụ th&ocirc;ng thường v&agrave; kh&ocirc;ng chơi game.</p>\r\n\r\n<ul>\r\n	<li>Thời lượng pin: 5 tiếng 21 ph&uacute;t.</li>\r\n	<li>Thời gian đo: 1 tiếng.</li>\r\n	<li>M&agrave;n h&igrave;nh 100%, &acirc;m lượng 70% v&agrave; kh&ocirc;ng bật chế độ tiết kiệm pin.</li>\r\n	<li>C&oacute; bật đ&egrave;n nền ở b&agrave;n ph&iacute;m.</li>\r\n	<li>C&oacute; sử dụng cổng USB để cắm b&agrave;n ph&iacute;m rời, chuột.</li>\r\n</ul>\r\n\r\n<p>- Trường hợp 2: D&ugrave;ng c&aacute;c t&aacute;c vụ th&ocirc;ng thường v&agrave; c&oacute; chơi game 20 ph&uacute;t:</p>\r\n\r\n<p><img alt=\"Kiểm tra thời lượng pin Acer Aspire 3 A314 với tác vụ thông thường và có chơi game trong 20 phút\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/cogame-310823-233939-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/cogame-310823-233939-800-resize.jpg\" title=\"Kiểm tra thời lượng pin Acer Aspire 3 A314 với tác vụ thông thường và có chơi game trong 20 phút\" /></p>\r\n\r\n<p>Kiểm tra thời lượng pin Acer Aspire 3 A314 với t&aacute;c vụ th&ocirc;ng thường v&agrave; c&oacute; chơi game trong 20 ph&uacute;t.</p>\r\n\r\n<ul>\r\n	<li>Thời lượng pin: 3 tiếng 11 ph&uacute;t.</li>\r\n	<li>Thời gian đo: 1 tiếng.</li>\r\n	<li>M&agrave;n h&igrave;nh 100%, &acirc;m lượng 70% v&agrave; kh&ocirc;ng bật chế độ tiết kiệm pin.</li>\r\n	<li>C&oacute; bật đ&egrave;n nền ở b&agrave;n ph&iacute;m.</li>\r\n	<li>C&oacute; sử dụng cổng USB để cắm b&agrave;n ph&iacute;m rời, chuột.</li>\r\n</ul>\r\n\r\n<p>Nh&igrave;n chung, kh&ocirc;ng kh&oacute; để nhận thấy rằng giữa hai trường hợp sử dụng c&oacute; sự c&aacute;ch biệt thường lượng tương đối lớn, điều n&agrave;y đồng nghĩa với việc Acer Aspire 3 A315 c&oacute; thể hoạt động l&acirc;u hơn với c&aacute;c t&aacute;c vụ sử dụng cơ bản, l&agrave;m việc, giải tr&iacute; nhẹ nh&agrave;ng. C&ograve;n trong trường sử dụng để game kết hợp với c&aacute;c t&aacute;c vụ cơ bản th&igrave; thiết bị n&agrave;y sẽ đ&aacute;p ứng được khoảng hơn 3 tiếng sử dụng. Tuy nhi&ecirc;n, những con số thống k&ecirc; n&agrave;y cũng chỉ mang t&iacute;nh tham khảo nhất định c&ograve;n thời lượng sử dụng m&aacute;y thực tế sẽ c&oacute; thể thay đổi t&ugrave;y thuộc v&agrave;o c&aacute;c t&aacute;c vụ, phần mềm, c&aacute;c thiết lập... m&agrave; bạn thực hiện tr&ecirc;n m&aacute;y ở điều kiện thực tế nữa nh&eacute;.</p>\r\n\r\n<p><strong>- Acer Aspire 3 A315:</strong></p>\r\n\r\n<p>- Trường hợp 1: D&ugrave;ng c&aacute;c t&aacute;c vụ th&ocirc;ng thường v&agrave; kh&ocirc;ng chơi game:</p>\r\n\r\n<p><img alt=\"Kiểm tra thời lượng pin Acer Aspire 3 A315 với tác vụ thông thường và không chơi game\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/khonggame-310823-233320-800-resize.png\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/khonggame-310823-233320-800-resize.png\" title=\"Kiểm tra thời lượng pin Acer Aspire 3 A315 với tác vụ thông thường và không chơi game\" /></p>\r\n\r\n<p>Kiểm tra thời lượng pin Acer Aspire 3 A315 với t&aacute;c vụ th&ocirc;ng thường v&agrave; kh&ocirc;ng chơi game.</p>\r\n\r\n<ul>\r\n	<li>Thời lượng pin: 4 tiếng 2 ph&uacute;t.</li>\r\n	<li>Thời gian đo: 1 tiếng.</li>\r\n	<li>M&agrave;n h&igrave;nh 100%, &acirc;m lượng 70% v&agrave; kh&ocirc;ng bật chế độ tiết kiệm pin.</li>\r\n	<li>C&oacute; bật đ&egrave;n nền ở b&agrave;n ph&iacute;m.</li>\r\n	<li>C&oacute; sử dụng cổng USB để cắm b&agrave;n ph&iacute;m rời, chuột.</li>\r\n</ul>\r\n\r\n<p>- Trường hợp 2: D&ugrave;ng c&aacute;c t&aacute;c vụ th&ocirc;ng thường v&agrave; c&oacute; chơi game 20 ph&uacute;t:</p>\r\n\r\n<p><img alt=\"Kiểm tra thời lượng pin Acer Aspire 3 A315 với tác vụ thông thường và có chơi game trong 20 phút\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/cogame-310823-233423-800-resize.png\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/cogame-310823-233423-800-resize.png\" title=\"Kiểm tra thời lượng pin Acer Aspire 3 A315 với tác vụ thông thường và có chơi game trong 20 phút\" /></p>\r\n\r\n<p>Kiểm tra thời lượng pin Acer Aspire 3 A315 với t&aacute;c vụ th&ocirc;ng thường v&agrave; c&oacute; chơi game trong 20 ph&uacute;t.</p>\r\n\r\n<ul>\r\n	<li>Thời lượng pin: 2 tiếng 49 ph&uacute;t.</li>\r\n	<li>Thời gian đo: 1 tiếng.</li>\r\n	<li>M&agrave;n h&igrave;nh 100%, &acirc;m lượng 70% v&agrave; kh&ocirc;ng bật chế độ tiết kiệm pin.</li>\r\n	<li>C&oacute; bật đ&egrave;n nền ở b&agrave;n ph&iacute;m.</li>\r\n	<li>C&oacute; sử dụng cổng USB để cắm b&agrave;n ph&iacute;m rời, chuột.</li>\r\n</ul>\r\n\r\n<p>Tương tự như Acer Aspire 3 A314, phi&ecirc;n bản 15.6 inch Acer Aspire 3 A315 cũng đ&aacute;p ứng thời lượng pin ấn tượng v&agrave; c&oacute; sự ch&ecirc;nh lệch lớn giữa hai trường hợp sử dụng. Cụ thể, ở việc hoạt động với c&aacute;c t&aacute;c vụ th&ocirc;ng thường th&igrave; thiết bị sẽ đ&aacute;p ứng được đến 4 giờ sử dụng, trong khi đ&oacute; việc giải tr&iacute; với những tựa game l&agrave;m giảm thời lượng đi c&ograve;n khoảng gần 3 giờ. Nếu so s&aacute;nh với phi&ecirc;n bản A314 th&igrave; Acer Aspire 3 A315 c&oacute; mức độ ti&ecirc;u thụ điện năng nhiều hơn nhưng lại kh&ocirc;ng qu&aacute; ảnh hưởng đến tiến tr&igrave;nh sử dụng h&agrave;ng ng&agrave;y của m&igrave;nh.</p>\r\n\r\n<h3><strong>Acer Aspire 3 A314 v&agrave; A315 cho khả năng tản nhiệt tốt</strong></h3>\r\n\r\n<p>Ngo&agrave;i thời lượng pin l&acirc;u d&agrave;i, tản nhiệt l&agrave; một trong những yếu tố quyết định hiệu quả l&agrave;m việc của một chiếc laptop, đặc biệt l&agrave; những mẫu m&aacute;y c&oacute; ngoại h&igrave;nh mỏng nhẹ như&nbsp;Acer Aspire 3 A314 v&agrave; A315. Để&nbsp;c&aacute;c bạn c&oacute; c&aacute;i nh&igrave;n trực quan hơn về khả năng tản nhiệt của bộ đ&ocirc;i laptop, m&igrave;nh c&oacute; sử dụng phần mềm HWiNFO64 để kiểm tra v&agrave; bạn c&oacute; thể tham khảo c&aacute;c kết quả ở dưới đ&acirc;y.</p>\r\n\r\n<p><strong>- Acer Aspire 3 A314:</strong></p>\r\n\r\n<p>Đo nhiệt độ CPU v&agrave; GPU của Acer Aspire 3 A314.</p>\r\n\r\n<p>Tuy ngoại h&igrave;nh mỏng manh, gọn g&agrave;ng của Acer Aspire 3 A314 c&oacute; thể sẽ l&agrave;m hạn chế rất nhiều cho khả năng tản nhiệt nhưng sau kiểm tra, m&igrave;nh nhận thấy thiết bị lại cho hiệu suất giải nhiệt rất ấn tượng khi c&oacute; thể hoạt động trong khoảng 53 - 55 độ (CPU) v&agrave; 47 - 60 (GPU). Đối với việc chơi game, thiết bị c&oacute; thể sẽ ấm l&ecirc;n một ch&uacute;t ở phần gầm m&aacute;y v&agrave; cạnh sau n&ecirc;n hầu như kh&ocirc;ng g&acirc;y ảnh hưởng qu&aacute; nhiều trong việc trải nghiệm giải tr&iacute; của m&igrave;nh.</p>\r\n\r\n<p><strong>- Acer Aspire 3 A315:</strong></p>\r\n\r\n<p><img alt=\"Đo nhiệt độ CPU và GPU của Acer Aspire 3 A315\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot28-010923-002925-800-resize.png\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/screenshot28-010923-002925-800-resize.png\" title=\"Đo nhiệt độ CPU và GPU của Acer Aspire 3 A315\" /></p>\r\n\r\n<p>Đo nhiệt độ CPU v&agrave; GPU của Acer Aspire 3 A315.</p>\r\n\r\n<p>Đến với Acer Aspire 3 A315, v&igrave; thiết bị c&oacute; k&iacute;ch thước n&ecirc;n c&oacute; nhiều lợi thế hơn về khả năng tản nhiệt. Trong suốt qu&aacute; tr&igrave;nh kiểm tra v&agrave; sử dụng c&aacute;c t&aacute;c vụ văn ph&ograve;ng, thiết bị đạt được mức nhiệt độ trong khoảng 40 độ C (CPU) v&agrave; 39 độ C (GPU), rất m&aacute;t mẻ cho một chiếc laptop c&oacute; ngoại h&igrave;nh mỏng nhẹ.</p>\r\n\r\n<h3><strong>Tổng kết</strong></h3>\r\n\r\n<p>Nh&igrave;n chung, với thiết kế gọn nhẹ,&nbsp; pin tốt, cấu h&igrave;nh tốt đ&aacute;p ứng nhiều t&aacute;c vụ c&ugrave;ng mức gi&aacute; dễ d&agrave;ng tiếp cận, m&igrave;nh nghĩ rằng bộ đ&ocirc;i laptop Acer Aspire 3 A314 v&agrave; A315 l&agrave; một sự lựa chọn tốt cho những người d&ugrave;ng c&oacute; nhu cầu kh&ocirc;ng qu&aacute; cao như học sinh, sinh vi&ecirc;n hoặc nh&acirc;n vi&ecirc;n văn ph&ograve;ng. Hiện tại, Acer Aspire 3 A314 v&agrave; A315 đang được kinh doanh ch&iacute;nh h&atilde;ng tại Thế Giới Di Động với gi&aacute; từ 9.99 triệu đồng v&agrave; 11.79 triệu đồng (gi&aacute; b&aacute;n c&oacute; thể thay đổi t&ugrave;y theo từng thời điểm v&agrave; chương tr&igrave;nh khuyến m&atilde;i).</p>\r\n\r\n<p><img alt=\"Acer Aspire 3 A314 và A315 đáp ứng tốt các nhu cầu làm việc và giải trí cơ bản\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-3-310823-152718-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/30/1545168/acer-aspire-a314-va-a315-3-310823-152718-800-resize.jpg\" title=\"Acer Aspire 3 A314 và A315 đáp ứng tốt các nhu cầu làm việc và giải trí cơ bản\" /></p>\r\n\r\n<p>Acer Aspire 3 A314 v&agrave; A315 đ&aacute;p ứng tốt c&aacute;c nhu cầu l&agrave;m việc v&agrave; giải tr&iacute; cơ bản.</p>\r\n\r\n<p>Vậy c&aacute;c bạn nghĩ như thế n&agrave;o về bộ đ&ocirc;i laptop Acer Aspire 3 A314 v&agrave; A315? H&atilde;y để lại &yacute; kiến b&ecirc;n dưới cho ch&uacute;ng m&igrave;nh được biết nh&eacute;! Cảm ơn c&aacute;c bạn đ&atilde; d&agrave;nh thời gian quan t&acirc;m v&agrave; theo d&otilde;i b&agrave;i viết của m&igrave;nh</p>\r\n');
INSERT INTO `tb_posts` (`post_id`, `post_title`, `post_img`, `slug`, `status`, `created_date`, `creator`, `post_content`) VALUES
(10, 'Đánh giá LG Gram 14 2023: Ngoại hình mỏng gọn, hiệu năng mạnh mẽ, thời lượng sử dụng pin tốt', 'list_post9.jpg', 'Đánh giá Acer Aspire 3 A314 36M 391A và A315 510P 32EF', 'Đã đăng', '2023-10-09 17:23:03', 'vuhonglinh123', '<h3><strong>LG Gram 14 2023 thiết kế gọn nhẹ, mức độ ho&agrave;n thiện chắc chắn</strong></h3>\r\n\r\n<p>Để n&oacute;i ch&iacute;nh x&aacute;c về LG Gram 14 2023 th&igrave; m&igrave;nh chỉ d&ugrave;ng đến 3 từ: Mỏng, gọn v&agrave; nhẹ. Mỏng, gọn ở đ&acirc;y được biểu thị qua k&iacute;ch thước tổng thể của sản phẩm chỉ&nbsp;312 x 213.9 x 16.8 mm. Do đ&oacute;, m&igrave;nh ho&agrave;n to&agrave;n c&oacute; thể cất sản phẩm v&agrave;o balo m&agrave; kh&ocirc;ng chiếm qu&aacute; nhiều diện t&iacute;ch để đồ b&ecirc;n trong. Th&ecirc;m v&agrave;o đ&oacute;, từ &#39;nhẹ&#39; ở đ&acirc;y &aacute;m chỉ c&acirc;n nặng của thiết bị chỉ 0.99 kg n&ecirc;n cảm gi&aacute;c cầm nắm tr&ecirc;n tay hay bỏ v&agrave;o balo để mang đi l&agrave;m/học hằng ng&agrave;y cực kỳ thoải m&aacute;i.</p>\r\n\r\n<p><img alt=\"LG Gram 14 2023 sở hữu thiết kế mỏng gọn và nhẹ nên mình có thể mang thiết bị đi làm cả ngày rất thoải mái\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-8-280823-202549-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-8-280823-202549-800-resize.jpg\" title=\"LG Gram 14 2023 sở hữu thiết kế mỏng gọn và nhẹ nên mình có thể mang thiết bị đi làm cả ngày rất thoải mái\" /></p>\r\n\r\n<p>LG Gram 14 2023 sở hữu thiết kế mỏng gọn v&agrave; nhẹ n&ecirc;n m&igrave;nh c&oacute; thể mang thiết bị đi l&agrave;m cả ng&agrave;y rất thoải m&aacute;i.</p>\r\n\r\n<p>Tuy mỏng, gọn, nhẹ nhưng LG Gram 14 2023 vẫn được LG ho&agrave;n thiện rất chắc chắn bằng&nbsp;hợp kim nh&ocirc;m v&agrave; magie. Ch&iacute;nh v&igrave; thế m&agrave; sản phẩm đạt được độ bền rất tốt gi&uacute;p bạn y&ecirc;n t&acirc;m sử dụng hơn trong c&ocirc;ng việc hằng ng&agrave;y. Th&ecirc;m v&agrave;o đ&oacute;, chiếc laptop n&agrave;y c&ograve;n được phủ lớp &aacute;o m&agrave;u đen n&ecirc;n mang lại cảm gi&aacute;c chắc chắn hơn khi bạn nh&igrave;n từ b&ecirc;n ngo&agrave;i. Tuy nhi&ecirc;n, lớp &aacute;o đen n&agrave;y lại rất dễ b&aacute;m mồ h&ocirc;i, dấu v&acirc;n tay khi d&ugrave;ng. Vậy n&ecirc;n, mọi người n&ecirc;n thường xuy&ecirc;n vệ sinh m&aacute;y để thiết bị lu&ocirc;n c&oacute; vẻ ngo&agrave;i sạch sẽ.</p>\r\n\r\n<p><img alt=\"LG Gram 14 2023 dù mỏng gọn nhưng vẫn có độ bền rất tốt\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-11-280823-202759-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-11-280823-202759-800-resize.jpg\" title=\"LG Gram 14 2023 dù mỏng gọn nhưng vẫn có độ bền rất tốt\" /></p>\r\n\r\n<p>LG Gram 14 2023 d&ugrave; mỏng gọn nhưng vẫn c&oacute; độ bền rất tốt.</p>\r\n\r\n<p>Một điểm m&igrave;nh đ&aacute;nh gi&aacute; cao LG Gram 14 2023 nữa l&agrave; những cổng kết nối đi k&egrave;m. Cụ thể, sản phẩm d&ugrave; mỏng gọn nhưng vẫn được LG cung cấp tương đối đầy đủ những cổng kết nối cần thiết cho người d&ugrave;ng. Đầu ti&ecirc;n, b&ecirc;n cạnh tr&aacute;i của sản phẩm chứa 1 cổng HDMI, 2 cổng USB-C (hỗ trợ USB, Power Delivery, DisplayPort v&agrave; Thunderbolt 4) v&agrave; 1 cổng cắm tai nghe chuẩn 3.5 mm. C&ograve;n ở b&ecirc;n phải l&agrave; 2 cổng USB-A, 1 cổng cắm thẻ nhớ&nbsp;MicroSD.</p>\r\n\r\n<p><img alt=\"Cạnh trái của LG Gram 14 2023\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-18-280823-202912-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-18-280823-202912-800-resize.jpg\" title=\"Cạnh trái của LG Gram 14 2023\" /></p>\r\n\r\n<p>Cạnh tr&aacute;i của LG Gram 14 2023.</p>\r\n\r\n<p><img alt=\"Cạnh phải của LG Gram 14 2023\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-19-280823-202916-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-19-280823-202916-800-resize.jpg\" title=\"Cạnh phải của LG Gram 14 2023\" /></p>\r\n\r\n<p>Cạnh phải của LG Gram 14 2023.</p>\r\n\r\n<p>Nhờ thế m&agrave; m&igrave;nh c&oacute; thể kết nối LG Gram 14 2023 với c&aacute;c thiết bị ngoại vi kh&aacute;c nhau như m&agrave;n h&igrave;nh LG Gram View+ 2023, chuột, b&agrave;n ph&iacute;m,.. để tối ưu h&oacute;a cho c&ocirc;ng việc.</p>\r\n\r\n<p><img alt=\"Với số lượng cổng kết nối như vậy thì mình có thể kết nối LG Gram 14 2023 với nhiều món phụ kiện khác nhau\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-view-plus-2023-56-280823-203118-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-view-plus-2023-56-280823-203118-800-resize.jpg\" title=\"Với số lượng cổng kết nối như vậy thì mình có thể kết nối LG Gram 14 2023 với nhiều món phụ kiện khác nhau\" /></p>\r\n\r\n<p>Với số lượng cổng kết nối như vậy th&igrave; m&igrave;nh c&oacute; thể kết nối LG Gram 14 2023 với nhiều m&oacute;n phụ kiện kh&aacute;c nhau.</p>\r\n\r\n<p>Phần bản lề của LG Gram 14 2023 cũng l&agrave; phần được LG ho&agrave;n thiện chắc chắn v&agrave; cải tiến tốt hơn so với thế hệ trước. Ch&iacute;nh v&igrave; thế m&agrave; m&igrave;nh c&oacute; thể mở m&aacute;y dễ d&agrave;ng bằng 1 tay hay khi g&otilde; ph&iacute;m, rung lắc nhẹ trong qu&aacute; tr&igrave;nh l&agrave;m việc th&igrave; kh&ocirc;ng bị x&ecirc; dịch một ch&uacute;t n&agrave;o.&nbsp;</p>\r\n\r\n<p><img alt=\"bản lề của LG Gram 14 2023 được hoàn thiện chắc chắn\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-37-280823-203317-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-37-280823-203317-800-resize.jpg\" title=\"bản lề của LG Gram 14 2023 được hoàn thiện chắc chắn\" /></p>\r\n\r\n<p>Bản lề của LG Gram 14 2023 được ho&agrave;n thiện chắc chắn.</p>\r\n\r\n<p>Đi s&acirc;u b&ecirc;n trong LG Gram 14 2023 th&igrave; do l&agrave; sản phẩm mỏng gọn, nhẹ n&ecirc;n được trang bị hệ thống b&agrave;n ph&iacute;m layout 67% quen thuộc. Sau một thời gian d&agrave;i trải nghiệm l&agrave;m việc th&igrave; m&igrave;nh nhận định b&agrave;n ph&iacute;m của chiếc laptop n&agrave;y l&agrave;m kh&ocirc;ng được tốt như m&igrave;nh kỳ vọng. Ch&iacute;nh x&aacute;c th&igrave; ph&iacute;m bấm hơi cứng, h&agrave;nh tr&igrave;nh kh&ocirc;ng đủ s&acirc;u n&ecirc;n cảm gi&aacute;c g&otilde; hơi khựng. Ch&iacute;nh v&igrave; thế m&agrave; tốc độ g&otilde; của m&igrave;nh tr&ecirc;n b&agrave;n ph&iacute;m n&agrave;y chậm hơn đ&aacute;ng kể so với những mẫu b&agrave;n ph&iacute;m kh&aacute;c m&agrave; m&igrave;nh từng d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"LG Gram 14 2023 sở hữu hệ thống bàn phím 67%\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-22-280823-203414-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-22-280823-203414-800-resize.jpg\" title=\"LG Gram 14 2023 sở hữu hệ thống bàn phím 67%\" /></p>\r\n\r\n<p>LG Gram 14 2023 sở hữu hệ thống b&agrave;n ph&iacute;m 67%.</p>\r\n\r\n<p><img alt=\"Với mình cảm giác gõ phím trên LG Gram 14 2023 với mình không quá tốt\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-32-280823-203608-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-32-280823-203608-800-resize.jpg\" title=\"Với mình cảm giác gõ phím trên LG Gram 14 2023 với mình không quá tốt\" /></p>\r\n\r\n<p>Với m&igrave;nh cảm gi&aacute;c g&otilde; ph&iacute;m tr&ecirc;n LG Gram 14 2023 với m&igrave;nh kh&ocirc;ng qu&aacute; tốt.</p>\r\n\r\n<p>D&ugrave; vậy, nếu mọi người kh&ocirc;ng qu&aacute; coi trọng việc trải nghiệm g&otilde; ph&iacute;m th&igrave; LG Gram 14 2023 vẫn c&oacute; thể mang lại cảm gi&aacute;c sử dụng tốt cho mọi người. C&ograve;n với m&igrave;nh th&igrave; buộc phải d&ugrave;ng b&agrave;n ph&iacute;m rời để c&oacute; được cảm gi&aacute;c sử dụng thoải m&aacute;i nhất. Điểm cộng của&nbsp;b&agrave;n ph&iacute;m c&ograve;n c&oacute; đ&egrave;n nền ở b&ecirc;n dưới n&ecirc;n m&igrave;nh vẫn c&oacute; thể sử dụng m&aacute;y thuận lợi trong nhiều m&ocirc;i trường &aacute;nh s&aacute;ng kh&aacute;c nhau.</p>\r\n\r\n<p><img alt=\"LG Gram 14 2023 còn hỗ trợ đèn nền\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-57-280823-203725-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-57-280823-203725-800-resize.jpg\" title=\"LG Gram 14 2023 còn hỗ trợ đèn nền\" /></p>\r\n\r\n<p>LG Gram 14 2023 c&ograve;n hỗ trợ đ&egrave;n nền.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, kh&ocirc;ng giống nhiều mẫu laptop ultrabook kh&aacute;c c&oacute; thể sử dụng Fn+ F1, F2 hay F3 m&agrave; c&aacute;c bạn buộc phải v&agrave;o phần mềm LG Smart Assistant bằng t&igrave;m kiếm hoặc nhấn Fn + F1 để bật cũng như thiết lập thời gian b&agrave;n ph&iacute;m tắt đ&egrave;n nhằm tiết kiệm pin. Ngo&agrave;i ra, LG Gram 14 2023 c&ograve;n hỗ trợ rất nhiều n&uacute;t tắt để bạn sử dụng m&aacute;y tiện lợi hơn như Fn + S (Power Saving Mode),.. bạn cũng c&oacute; thể tự thiết lập shortcut trong LG Smart Assistant với Fn + 1 hoặc 2, 3... để sử dụng nhanh một v&agrave;i t&iacute;nh năng cần thiết. Chưa kể, LG c&ograve;n hỗ trợ cho người d&ugrave;ng kh&ocirc;ng qu&aacute; r&agrave;nh t&iacute;nh năng ph&iacute;m tắt bằng c&aacute;ch nhấn giữ Fn hoặc WIndow để hiện bảng hướng dẫn. M&igrave;nh đ&aacute;nh gi&aacute; cao t&iacute;nh năng n&agrave;y, bởi nhờ vậy m&agrave; ch&uacute;ng ta kh&ocirc;ng phải mất qu&aacute; nhiều thời gian t&igrave;m kiếm tr&ecirc;n mạng.</p>\r\n\r\n<p><img alt=\"LG Gram 14 2023 còn hỗ trợ biểu mẫu shorcut để mình đỡ phải đi tìm kiếm trên mạng\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-14-2023-2-280823-204534-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-14-2023-2-280823-204534-800-resize.jpg\" title=\"LG Gram 14 2023 còn hỗ trợ biểu mẫu shorcut để mình đỡ phải đi tìm kiếm trên mạng\" /></p>\r\n\r\n<p>LG Gram 14 2023 c&ograve;n hỗ trợ biểu mẫu shortcut để m&igrave;nh đỡ phải đi t&igrave;m kiếm tr&ecirc;n mạng.</p>\r\n\r\n<p>Ở b&ecirc;n dưới b&agrave;n ph&iacute;m của LG Gram 14 2023 l&agrave; một chiếc b&agrave;n di chuột c&oacute; diện t&iacute;ch vừa phải. Độ ho&agrave;n thiện tốt như khi m&igrave;nh nhấn v&agrave;o kh&ocirc;ng c&oacute; cảm gi&aacute;c l&uacute;n qu&aacute; nhiều hay lọc xọc những b&ecirc;n c&ograve;n lại. Cảm gi&aacute;c di chuột mượt, độ phản hồi nhanh.</p>\r\n\r\n<h3><strong>LG Gram 14 2023 m&agrave;n h&igrave;nh hiển thị sắc n&eacute;t, trang bị nhiều cảm biến bảo mật tiện lợi</strong></h3>\r\n\r\n<p>Nhắc đến LG th&igrave; ch&uacute;ng ta phải nhắc đến những chiếc m&agrave;n h&igrave;nh, tivi được đ&aacute;nh gi&aacute; rất cao về khả năng hiển thị h&igrave;nh ảnh d&ugrave; ở mức gi&aacute; n&agrave;o. Điều n&agrave;y cũng kh&ocirc;ng phải ngoại lệ với LG Gram 14 2023 khi&nbsp;sở hữu cho m&igrave;nh một m&agrave;n h&igrave;nh c&oacute; k&iacute;ch thước 14, hoạt động tr&ecirc;n tấm nền IPS, độ ph&acirc;n giải Full HD+ (1.920 x 1.200 pixels) v&agrave; được c&acirc;n chỉnh m&agrave;u sắc rất tốt n&ecirc;n mang đến được những h&igrave;nh ảnh c&oacute; độ ph&acirc;n giải cao, g&oacute;c nh&igrave;n rộng r&atilde;i v&agrave; m&agrave;u sắc rực rỡ.</p>\r\n\r\n<p><img alt=\"LG Gram 14 2023 còn có chất lượng hiển thị rất tốt\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-69-280823-204819-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-69-280823-204819-800-resize.jpg\" title=\"LG Gram 14 2023 còn có chất lượng hiển thị rất tốt\" /></p>\r\n\r\n<p>LG Gram 14 2023 c&ograve;n c&oacute; chất lượng hiển thị rất tốt.</p>\r\n\r\n<p>Nhắc đến c&acirc;n chỉnh m&agrave;u th&igrave; m&agrave;n h&igrave;nh của LG Gram 14 2023 c&oacute; độ phủ m&agrave;u 99% chuẩn DCI-P3 gi&uacute;p cho ra h&igrave;nh ảnh c&oacute; m&agrave;u sắc được thể hiện ch&acirc;n thực v&agrave; ch&iacute;nh x&aacute;c. Vậy n&ecirc;n, sản phẩm ho&agrave;n to&agrave;n l&agrave; &#39;một người bạn&#39; rất đ&aacute;ng tin cậy cho những c&ocirc;ng việc li&ecirc;n quan đến đồ họa, thiết kế.&nbsp;</p>\r\n\r\n<p><img alt=\"LG Gram 14 2023 còn có độ phủ màu cao\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-107-280823-205008-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-107-280823-205008-800-resize.jpg\" title=\"LG Gram 14 2023 còn có độ phủ màu cao\" /></p>\r\n\r\n<p>LG Gram 14 2023 c&ograve;n c&oacute; độ phủ m&agrave;u cao.</p>\r\n\r\n<p>Ngo&agrave;i chất lượng hiển thị tốt, LG Gram 14 2023 c&ograve;n đi k&egrave;m với rất nhiều cảm biến b&ecirc;n cạnh webcam. Những cảm biến n&agrave;y hoạt động t&ugrave;y theo từng nhu cầu sử dụng kh&aacute;c nhau. Điểm đ&aacute;ng ch&uacute; &yacute; nhất l&agrave;&nbsp;c&ocirc;ng nghệ bảo mật gương mặt mới gi&uacute;p người d&ugrave;ng c&oacute; thể bảo mật nội dung hiển thị tr&ecirc;n m&agrave;n h&igrave;nh một c&aacute;ch hiệu quả. Về cơ bản, cảm biến n&agrave;y sẽ theo d&otilde;i h&agrave;nh động của bạn v&agrave; khi bạn rời khỏi vị tr&iacute; th&igrave; m&agrave;n h&igrave;nh sẽ tự động tắt để bảo vệ nội dung khỏi những người xung quanh.&nbsp;</p>\r\n\r\n<p><img alt=\"LG Gram 2023 được tích hợp công nghệ bảo mật khuôn mặt mới giúp nâng cao quyền riêng tư\" data-original=\"https://cdn.tgdd.vn/Files/2023/07/05/1537061/lg-gram-2023-49-050723-225843-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/07/05/1537061/lg-gram-2023-49-050723-225843-800-resize.jpg\" title=\"LG Gram 2023 được tích hợp công nghệ bảo mật khuôn mặt mới giúp nâng cao quyền riêng tư\" /></p>\r\n\r\n<p>LG Gram 2023 được t&iacute;ch hợp c&ocirc;ng nghệ bảo mật khu&ocirc;n mặt mới gi&uacute;p n&acirc;ng cao quyền ri&ecirc;ng tư.</p>\r\n\r\n<p><img alt=\"Các bạn có thể cài đặt cảm biến thông qua LG Security Guard\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-87-280823-205147-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-87-280823-205147-800-resize.jpg\" title=\"Các bạn có thể cài đặt cảm biến thông qua LG Security Guard\" /></p>\r\n\r\n<p>C&aacute;c bạn c&oacute; thể c&agrave;i đặt cảm biến th&ocirc;ng qua LG Security Guard.</p>\r\n\r\n<p><img alt=\"Ví dụ thiết bị có thể cảnh báo bạn nên nghĩ ngơi một chút khi làm việc lâu\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-14-2023-16-280823-205011-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-14-2023-16-280823-205011-800-resize.jpg\" title=\"Ví dụ thiết bị có thể cảnh báo bạn nên nghĩ ngơi một chút khi làm việc lâu\" /></p>\r\n\r\n<p>V&iacute; dụ thiết bị c&oacute; thể cảnh b&aacute;o bạn n&ecirc;n nghỉ ngơi một ch&uacute;t khi l&agrave;m việc l&acirc;u.</p>\r\n\r\n<p>Bạn c&oacute; thể điều chỉnh cảm biến n&agrave;y th&ocirc;ng qua ứng dụng LG Security Guard. Chưa kể, bạn c&ograve;n c&oacute; thể điều chỉnh độ nhạy của cảm biến tr&ecirc;n LG Gram 14 2023 để sử dụng sao cho ph&ugrave; hợp với nhu cầu sử dụng của bản th&acirc;n.</p>\r\n\r\n<h3><strong>LG Gram 14 2023 hiệu năng mạnh mẽ với chip Intel Core i7 Gen 13th</strong></h3>\r\n\r\n<p>Trước khi đi v&agrave;o chi tiết, m&igrave;nh sẽ điểm qua th&ocirc;ng số cấu h&igrave;nh của LG Gram 2023 cho c&aacute;c bạn tiện theo d&otilde;i nh&eacute;!</p>\r\n\r\n<ul>\r\n	<li>M&agrave;n h&igrave;nh: K&iacute;ch thước 14 inch, độ ph&acirc;n giải Full HD+ (1.920 x 1.200 pixels).</li>\r\n	<li>Vi xử l&yacute;: Intel Core i7-1360P.</li>\r\n	<li>RAM: 16 GB LPDDR5.</li>\r\n	<li>Dung lượng ổ cứng: 512 GB SSD NVMe PCIe Gen 4.0, hỗ trợ th&ecirc;m 1 khe cắm mở rộng.</li>\r\n	<li>Card đồ họa: Intel Iris Xe Graphics.</li>\r\n	<li>Pin: 65 WHrs.</li>\r\n	<li>Hệ điều h&agrave;nh: Windows 11.</li>\r\n</ul>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/chip-intel-the-he-13-la-gi-1495418\" target=\"_blank\" title=\"Mới nhất chip Intel thế hệ 13 Raptor Lake: Hiệu suất tối ưu, mạnh mẽ\" type=\"Mới nhất chip Intel thế hệ 13 Raptor Lake: Hiệu suất tối ưu, mạnh mẽ\">Mới nhất chip Intel thế hệ 13 Raptor Lake: Hiệu suất tối ưu, mạnh mẽ</a></p>\r\n\r\n<p>LG Gram 2023 sở hữu th&ocirc;ng số cấu h&igrave;nh ấn tượng.</p>\r\n\r\n<p>Như mọi người thấy ở bảng thống kế tr&ecirc;n, LG Gram 14 2023 được trang bị con chip Intel Core i7-1360P của nh&agrave; Intel.&nbsp;Đ&acirc;y l&agrave; con chip thuộc d&ograve;ng P tiết kiệm điện được cấu th&agrave;nh từ 12 nh&acirc;n xử l&yacute;, gồm 4 nh&acirc;n P-Core hiệu năng cao v&agrave; 8 nh&acirc;n E-Core tiết kiệm điện với xung nhịp tối đa c&oacute; thể đạt đến 5.0 GHz. Vậy n&ecirc;n, d&ugrave; l&agrave; d&ograve;ng P tiết kiệm điện năng nhưng con chip n&agrave;y vẫn c&oacute; thể cung cấp một hiệu năng đủ mạnh cho bạn l&agrave;m việc cũng như giải tr&iacute; với những tựa game Esports phổ biến hiện nay.</p>\r\n\r\n<p><img alt=\"Với Intel i7 - 1360P thì LG Gram 14 2023 có thể đáp ứng tốt mọi nhu cầu sử dụng của người dùng\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-112-280823-205453-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-112-280823-205453-800-resize.jpg\" title=\"Với Intel i7 - 1360P thì LG Gram 14 2023 có thể đáp ứng tốt mọi nhu cầu sử dụng của người dùng\" /></p>\r\n\r\n<p>Với Intel i7 - 1360P th&igrave; LG Gram 14 2023 c&oacute; thể đ&aacute;p ứng tốt mọi nhu cầu sử dụng của người d&ugrave;ng.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;,&nbsp;LG Gram 14 2023 c&ograve;n được trang bị dung lượng RAM l&ecirc;n đến 16 GB v&agrave; card đồ họa t&iacute;ch hợp Intel Iris Xe Graphics&nbsp;n&ecirc;n ho&agrave;n to&agrave;n đ&aacute;p ứng tốt c&aacute;c t&aacute;c vụ l&agrave;m việc v&agrave; giải tr&iacute; của bạn, mang đến khả năng đa nhiệm tốt, đ&oacute;ng mở ứng dụng mượt m&agrave;.</p>\r\n\r\n<p><img alt=\"LG Gram 14 2023 còn mang đến trải nghiệm đa nhiệm mượt mà\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-113-280823-205838-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-113-280823-205838-800-resize.jpg\" title=\"LG Gram 14 2023 còn mang đến trải nghiệm đa nhiệm mượt mà\" /></p>\r\n\r\n<p>LG Gram 14 2023 c&ograve;n mang đến trải nghiệm đa nhiệm mượt m&agrave;.</p>\r\n\r\n<p>Để c&aacute;c bạn c&oacute; một c&aacute;i nh&igrave;n trực quan hơn về hiệu năng của LG Gram 14 2023 n&agrave;y th&igrave; m&igrave;nh đ&atilde; kiểm tra nhanh hiệu năng của em n&oacute; th&ocirc;ng qua c&aacute;c qua phần mềm như Geekbench 6, Cinebench R23, 3DMark v&agrave; PCMark 10. B&ecirc;n cạnh đ&oacute;, m&igrave;nh c&ograve;n bật chế độ hiệu suất cao th&ocirc;ng qua ứng dụng LG Assistant.</p>\r\n\r\n<p>Để kiểm tra sức mạnh của LG Gram 14 2023 th&igrave; m&igrave;nh đ&atilde; bật chế độ hiệu suất cao.</p>\r\n\r\n<p>Kết quả m&igrave;nh thu được như sau:</p>\r\n\r\n<p>- GeekBench 6:</p>\r\n\r\n<ul>\r\n	<li>Đơn nh&acirc;n/đa nh&acirc;n: 2.074 điểm/7.621 điểm.</li>\r\n	<li>​GPU Compute: 14.329 điểm.</li>\r\n</ul>\r\n\r\n<p>Điểm GeekBench 6 của LG Gram 14 2023.</p>\r\n\r\n<p>- Cinebench R23 (đa nh&acirc;n/đơn nh&acirc;n): 7.326 điểm/1.310 điểm.</p>\r\n\r\n<p>Điểm Cinebench R23 của LG Gram 14 2023.</p>\r\n\r\n<p>- PCMark 10: 4.816 điểm.</p>\r\n\r\n<p><img alt=\"Điểm PCMark 10 của LG Gram 14 2023\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/screenshot57-280823-201437-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/screenshot57-280823-201437-800-resize.jpg\" title=\"Điểm PCMark 10 của LG Gram 14 2023\" /></p>\r\n\r\n<p>Điểm PCMark 10 của LG Gram 14 2023.</p>\r\n\r\n<p>- 3DMark Time Spy: 1.288 điểm.</p>\r\n\r\n<p><img alt=\"Điểm 3DMark Time Spy của LG Gram 14 2023\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/screenshot59-280823-201440-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/screenshot59-280823-201440-800-resize.jpg\" title=\"Điểm 3DMark Time Spy của LG Gram 14 2023\" /></p>\r\n\r\n<p>Điểm 3DMark Time Spy của LG Gram 14 2023.</p>\r\n\r\n<p>Nh&igrave;n chung th&igrave; những điểm số hiệu năng của LG Gram 14 2023 l&agrave; rất tốt v&agrave; năm ở mức cạnh tranh c&ugrave;ng c&aacute;c thiết bị trong c&ugrave;ng ph&acirc;n kh&uacute;c gi&aacute;. Điều n&agrave;y c&ograve;n được thể hiện th&ocirc;ng qua c&aacute;c trải nghiệm thực tế m&igrave;nh nhận thấy m&aacute;y hoạt động mượt m&agrave;, ổn định v&agrave; đ&aacute;p ứng tốt c&aacute;c t&aacute;c vụ m&igrave;nh thực hiện tr&ecirc;n thiết bị.</p>\r\n\r\n<p><img alt=\"LG Gram 14 2023 đạt điểm hiệu năng rất tốt trong phân khúc\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-14-2023-4-280823-210353-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-14-2023-4-280823-210353-800-resize.jpg\" title=\"LG Gram 14 2023 đạt điểm hiệu năng rất tốt trong phân khúc\" /></p>\r\n\r\n<p>LG Gram 14 2023 đạt điểm hiệu năng rất tốt trong ph&acirc;n kh&uacute;c.</p>\r\n\r\n<p>B&ecirc;n cạnh việc chấm điểm hiệu năng, m&igrave;nh cũng chơi thử hai tựa game l&agrave; FIFA Online 4 v&agrave; Li&ecirc;n Minh Huyền Thoại tr&ecirc;n LG Gram 14 2023. Ở cả hai game n&agrave;y m&igrave;nh đều chỉnh đồ họa ở mức ph&ugrave; hợp với thiết bị v&agrave; sử dụng phần mềm MSI Afterburner để đo FPS, nhiệt độ CPU, mức RAM ti&ecirc;u thụ,... cho c&aacute;c bạn c&oacute; một c&aacute;i nh&igrave;n trực quan hơn.</p>\r\n\r\n<ul>\r\n	<li><strong>FIFA Online 4</strong></li>\r\n</ul>\r\n\r\n<p>Ở tựa game n&agrave;y sau nhiều lần thử nghiệm ở c&aacute;c mốc đồ họa kh&aacute;c nhau th&igrave; m&igrave;nh đ&atilde; quyết định chọn Rất Thấp v&agrave; độ ph&acirc;n giải l&agrave; 1.440 x 900 (16:10).</p>\r\n\r\n<p>Thiết lập đồ họa FIFA Online 4 m&agrave; m&igrave;nh &aacute;p dụng tr&ecirc;n LG Gram 14 2023.</p>\r\n\r\n<p>Với chế độ n&agrave;y trải nghiệm chơi game tr&ecirc;n LG Gram 14 2023 l&agrave; mượt m&agrave;, ổn định nhất với FPS duy tr&igrave; ở mức 50 - 60 FPS d&ugrave; t&igrave;nh trạng giật lag vẫn c&oacute; xảy ra nhưng kh&ocirc;ng qu&aacute; nhiều. C&ograve;n nếu cao hơn th&igrave; giật lag xảy ra thường xuy&ecirc;n hơn khiến cảm gi&aacute;c chơi của bạn sẽ rất kh&oacute; chịu.</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.thegioididong.com/tin-tuc/danh-gia-lg-gram-14-2023-1544783\" /></p>\r\n\r\n<p>Trải nghiệm FIFA Online 4 tr&ecirc;n LG Gram 14 2023.</p>\r\n\r\n<ul>\r\n	<li><strong>Li&ecirc;n Minh Huyền Thoại</strong></li>\r\n</ul>\r\n\r\n<p>Tiếp theo l&agrave; Li&ecirc;n Minh Huyền Thoại v&agrave; LG Gram 14 2023 vẫn thể hiện tốt trong việc xử l&yacute; tr&ograve; chơi n&agrave;y d&ugrave; thiết lập đồ họa m&agrave; m&igrave;nh đặt l&agrave; cao nhất.</p>\r\n\r\n<p><img alt=\"Thiết lập đồ họa Liên Minh Huyền Thoại trên LG Gram 14 2023\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/leagueoflegendstmclient8_28_202311_09_39am-280823-201556-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/leagueoflegendstmclient8_28_202311_09_39am-280823-201556-800-resize.jpg\" title=\"Thiết lập đồ họa Liên Minh Huyền Thoại trên LG Gram 14 2023\" /></p>\r\n\r\n<p>Thiết lập đồ họa Li&ecirc;n Minh Huyền Thoại tr&ecirc;n LG Gram 14 2023.</p>\r\n\r\n<p>M&igrave;nh nhận được một trải nghiệm chơi game mượt m&agrave; với FPS trung b&igrave;nh khoảng 115 FPS. D&ugrave; mức FPS c&oacute; sự biến động nhưng trải nghiệm của m&igrave;nh l&agrave; rất mượt m&agrave;, đ&atilde; mắt với c&aacute;c hiệu ứng chuyển động trơn tru. Đồ họa trong game vẫn được t&aacute;i tạo tốt, c&aacute;c khung cảnh, chi tiết nh&acirc;n vật được thể hiện đẹp mắt.</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.thegioididong.com/tin-tuc/danh-gia-lg-gram-14-2023-1544783\" /></p>\r\n\r\n<p>Trải nghiệm Li&ecirc;n Minh Huyền Thoại tr&ecirc;n LG Gram 14 2023.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; th&igrave; m&igrave;nh cũng sử dụng phần mềm Crystal Diskmark Shizuku để đo tốc độ đọc/ghi của ổ cứng SSD b&ecirc;n trong LG Gram 14 2023. Kết quả m&igrave;nh thu được cũng rất ấn tượng, cụ thể l&agrave; như sau:</p>\r\n\r\n<p><img alt=\"Điểm Crystal Diskmask Shizuku của LG Gram 14 2023\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/screenshot99-280823-201728-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/screenshot99-280823-201728-800-resize.jpg\" title=\"Điểm Crystal Diskmask Shizuku của LG Gram 14 2023\" /></p>\r\n\r\n<p>Điểm Crystal Diskmark Shizuku của LG Gram 14 2023.</p>\r\n\r\n<ul>\r\n	<li>Tốc độ đọc: 6.891 MB/s.</li>\r\n	<li>Tốc độ ghi: 1.609 MB/s.</li>\r\n</ul>\r\n\r\n<p>Những kết quả tr&ecirc;n cho thấy tốc độ đọc/ghi dữ liệu của LG Gram 14 2023 tương đối nhanh, gi&uacute;p ch&uacute;ng ta c&oacute; thể di chuyển dữ liệu từ m&aacute;y ra c&aacute;c thiết bị lưu trữ trong thời gian nhanh ch&oacute;ng m&agrave; kh&ocirc;ng phải chờ qu&aacute; l&acirc;u.</p>\r\n\r\n<h3><strong>LG Gram 14 2023 thời lượng sử dụng pin l&acirc;u d&agrave;i, tản nhiệt tốt</strong></h3>\r\n\r\n<p>Như m&igrave;nh c&oacute; đề cập ở mục cấu h&igrave;nh, LG Gram 14 2023 được trang bị vi xử l&yacute; Intel Core i7-1360P, một con chip tiết kiệm điện năng n&ecirc;n thiết bị ho&agrave;n to&agrave;n c&oacute; thể đ&aacute;p ứng thời gian sử dụng l&acirc;u d&agrave;i. Ch&iacute;nh v&igrave; thế m&agrave;, m&igrave;nh c&oacute; thể sử dụng thiết bị trong khoảng 6 - 7 tiếng li&ecirc;n tục m&agrave; kh&ocirc;ng cần phải cắm sạc. Để l&agrave;m r&otilde; hơn th&igrave; m&igrave;nh đ&atilde; sử dụng phần mềm BatteryMon để test v&agrave; c&oacute; chia ra l&agrave;m 2 trường hợp sử dụng kh&aacute;c nhau, kết quả m&igrave;nh thu được như sau:</p>\r\n\r\n<p>- Trường hợp 1: D&ugrave;ng c&aacute;c t&aacute;c vụ th&ocirc;ng thường v&agrave; kh&ocirc;ng chơi game:</p>\r\n\r\n<p><img alt=\"Kết quả đo Batterymon không chơi game\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/kochoigame-280823-201900-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/kochoigame-280823-201900-800-resize.jpg\" title=\"Kết quả đo Batterymon không chơi game\" /></p>\r\n\r\n<p>Kết quả đo BatteryMon kh&ocirc;ng chơi game.</p>\r\n\r\n<ul>\r\n	<li>Thời lượng pin: 4 giờ 59 ph&uacute;t.</li>\r\n	<li>Thời gian đo: 1 tiếng.</li>\r\n	<li>M&agrave;n h&igrave;nh 100%, &acirc;m lượng 70% v&agrave; kh&ocirc;ng bật chế độ tiết kiệm pin.</li>\r\n	<li>Kh&ocirc;ng bật đ&egrave;n nền ở b&agrave;n ph&iacute;m.</li>\r\n	<li>C&oacute; sử dụng cổng USB để cắm chuột, b&agrave;n ph&iacute;m.</li>\r\n</ul>\r\n\r\n<p>- Trường hợp 2: D&ugrave;ng c&aacute;c t&aacute;c vụ th&ocirc;ng thường v&agrave; c&oacute; chơi game:</p>\r\n\r\n<p><img alt=\"Kết quả đo BatteryMon có chơi game 20 phút\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/cochoigame-280823-201905-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/cochoigame-280823-201905-800-resize.jpg\" title=\"Kết quả đo BatteryMon có chơi game 20 phút\" /></p>\r\n\r\n<p>Kết quả đo BatteryMon c&oacute; chơi game 20 ph&uacute;t.</p>\r\n\r\n<ul>\r\n	<li>Thời lượng pin: 3 giờ 26 ph&uacute;t.</li>\r\n	<li>Thời gian đo: 1 tiếng.</li>\r\n	<li>M&agrave;n h&igrave;nh 100%, &acirc;m lượng 70% v&agrave; kh&ocirc;ng bật chế độ tiết kiệm pin.</li>\r\n	<li>Kh&ocirc;ng bật đ&egrave;n nền ở b&agrave;n ph&iacute;m.</li>\r\n	<li>C&oacute; sử dụng cổng USB để cắm chuột, b&agrave;n ph&iacute;m.</li>\r\n</ul>\r\n\r\n<p>C&oacute; thể thấy, thời gian m&agrave; m&igrave;nh đo được qua phần mềm tr&ecirc;n kh&ocirc;ng kh&aacute;c biệt qu&aacute; nhiều so với khoảng thời gian m&agrave; m&igrave;nh sử dụng tr&ecirc;n LG Gram 14 2023. Vậy n&ecirc;n, d&ugrave; c&oacute; qu&ecirc;n mang sạc th&igrave; bạn vẫn c&oacute; thể sử dụng trong cả một ng&agrave;y nếu ph&acirc;n chia c&ocirc;ng việc một c&aacute;ch hợp l&yacute;. B&ecirc;n cạnh đ&oacute; do l&agrave; sử dụng cổng USB-C n&ecirc;n m&igrave;nh c&oacute; thể d&ugrave;ng chung củ sạc Ugreen 65 W với điện thoại n&ecirc;n rất tiện cho việc mang đi l&agrave;m.</p>\r\n\r\n<p><img alt=\"LG Gram 14 2023 có thể mang lại thời lượng sử dụng lâu dài cho người dùng\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-66-280823-210637-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-66-280823-210637-800-resize.jpg\" title=\"LG Gram 14 2023 có thể mang lại thời lượng sử dụng lâu dài cho người dùng\" /></p>\r\n\r\n<p>LG Gram 14 2023 c&oacute; thể mang lại thời lượng sử dụng l&acirc;u d&agrave;i cho người d&ugrave;ng.</p>\r\n\r\n<p>Tiếp đến m&igrave;nh cũng đ&atilde; d&ugrave;ng th&ecirc;m phần mềm HWiNFO64 nhằm kiểm tra khả năng tản nhiệt của LG Gram 14 2023. Kết quả m&igrave;nh thu được như sau:</p>\r\n\r\n<p><img alt=\"Kết quả đo HWinFO4 của LG Gram 14 2023\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/screenshot77-280823-202024-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/screenshot77-280823-202024-800-resize.jpg\" title=\"Kết quả đo HWinFO4 của LG Gram 14 2023\" /></p>\r\n\r\n<p>Kết quả đo HWinFO4 của LG Gram 14 2023.</p>\r\n\r\n<p>Qua bảng kết quả b&ecirc;n tr&ecirc;n, ch&uacute;ng ta c&oacute; thể thấy mức nhiệt cao nhất m&agrave; LG Gram 14 2023 cho ra l&agrave; 77 độ C v&agrave; đ&acirc;y cũng l&agrave; nhiệt độ khi m&igrave;nh chơi game. C&ograve;n khi d&ugrave;ng b&igrave;nh thường th&igrave; thiết bị giao động ở mức 45 - 58 độ C. Đ&acirc;y l&agrave; những mốc nhiệt b&igrave;nh thường v&agrave; m&aacute;t mẻ gi&uacute;p bạn sử dụng thoải m&aacute;i trong thời gian d&agrave;i.</p>\r\n\r\n<h3><strong>Tổng kết</strong></h3>\r\n\r\n<p>Nh&igrave;n chung LG Gram 14 2023 nếu loại trừ phần b&agrave;n ph&iacute;m l&agrave;m chưa ổn th&igrave; tất cả những thứ c&ograve;n lại đều được LG l&agrave;m rấy tốt từ ngoại h&igrave;nh cho đến hiệu năng lẫn pin. Ch&iacute;nh v&igrave; thế m&agrave; m&igrave;nh nghĩ đ&acirc;y sẽ l&agrave; một người bạn đồng h&agrave;nh tốt cho c&ocirc;ng việc thường ng&agrave;y của mỗi người.&nbsp;Hiện tại, LG Gram 2023 đang được kinh doanh ch&iacute;nh h&atilde;ng tại Thế Giới Di Động với gi&aacute; từ 38.99 triệu đồng (c&oacute; thể thay đổi t&ugrave;y chương tr&igrave;nh khuyến m&atilde;i), tuy cao nhưng ph&ugrave; hợp với những g&igrave; m&agrave; sản phẩm n&agrave;y mang lại.</p>\r\n\r\n<p><img alt=\"LG Gram 14 2023 dù có mức giá cao nhưng rất hợp lý\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-61-280823-210818-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544783/lg-gram-2023-61-280823-210818-800-resize.jpg\" title=\"LG Gram 14 2023 dù có mức giá cao nhưng rất hợp lý\" /></p>\r\n\r\n<p>LG Gram 14 2023 d&ugrave; c&oacute; mức gi&aacute; cao nhưng ph&ugrave; hợp với những g&igrave; m&agrave; m&aacute;y sở hữu.</p>\r\n\r\n<p>Vậy c&aacute;c bạn c&oacute; đ&aacute;nh gi&aacute; g&igrave; về LG Gram 14 2023 kh&ocirc;ng? H&atilde;y để lại cảm nghĩ ở phần b&igrave;nh luận b&ecirc;n dưới cho m&igrave;nh v&agrave; mọi người được biết nha! Cảm ơn c&aacute;c bạn đ&atilde; quan t&acirc;m v&agrave; theo d&otilde;i b&agrave;i viết của m&igrave;nh.</p>\r\n'),
(11, 'Đừng để ưu đãi ngon chìm, iPhone, Samsung, OPPO đua nhau giảm đến 13 triệu', 'list_post10.jpg', 'Đừng để ưu đãi ngon chìm', 'Đã đăng', '2023-10-09 17:23:03', 'vuhonglinh123', '<p><img alt=\"iPhone, Samsung, OPPO đua nhau giảm đến 13 triệu\" data-original=\"https://cdn.tgdd.vn/Files/2023/08/28/1544812/ip14-280823-182435-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/08/28/1544812/ip14-280823-182435-800-resize.jpg\" title=\"iPhone, Samsung, OPPO đua nhau giảm đến 13 triệu\" /></p>\r\n\r\n<h2>Deal ngon&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"smartphone\" type=\"smartphone\">smartphone</a>&nbsp;đầu th&aacute;ng nhất định bạn kh&ocirc;ng được bỏ lỡ. Khuyến m&atilde;i cực sốc chỉ c&oacute; tại&nbsp;<a href=\"https://www.thegioididong.com/\" target=\"_blank\" title=\"Thế Giới Di Động\" type=\"Thế Giới Di Động\">Thế Giới Di Động</a>&nbsp;th&ocirc;i, giảm gi&aacute; đến 13 triệu đồng, c&oacute; th&ecirc;m hỗ trợ trả g&oacute;p 0%, bạn c&oacute; thể mua sắm với gi&aacute; tốt v&agrave; tiện lợi hơn. Nhanh tay xem ngay điện thoại giảm gi&aacute; ngay sau đ&acirc;y!</h2>\r\n\r\n<p><strong>Thời gian khuyến m&atilde;i:&nbsp;</strong>Từ ng&agrave;y 25/08 - 05/09/2023.</p>\r\n\r\n<p><strong>Lưu &yacute;:</strong></p>\r\n\r\n<ul>\r\n	<li>Khuyến m&atilde;i c&oacute; thể kết th&uacute;c sớm trước thời hạn nếu hết số lượng sản phẩm hoặc th&ocirc;ng tin khuyến m&atilde;i c&oacute; thay đổi.</li>\r\n	<li>Gi&aacute; v&agrave; khuyến m&atilde;i hiện tại &aacute;p dụng cho&nbsp;<strong>khu vực Hồ Ch&iacute; Minh</strong>, c&oacute; thể kh&aacute;c so với c&aacute;c tỉnh th&agrave;nh kh&aacute;c. Kh&aacute;ch h&agrave;ng cần chọn khu vực m&igrave;nh sinh sống để xem gi&aacute; v&agrave; khuyến m&atilde;i ch&iacute;nh x&aacute;c nhất.</li>\r\n	<li>&Ocirc; sản phẩm chưa hiển thị ưu đ&atilde;i ch&iacute;nh x&aacute;c,&nbsp;để hiện ưu đ&atilde;i ch&iacute;nh x&aacute;c, kh&aacute;ch cần&nbsp;bấm&nbsp;<strong>Xem chi tiết.</strong></li>\r\n</ul>\r\n\r\n<h3><strong>OPPO Find X5 Pro 5G: Giảm gi&aacute; 13 triệu đồng&nbsp;+ Trả g&oacute;p 0%, gi&aacute; c&ograve;n 19.99 triệu đồng</strong></h3>\r\n\r\n<p><img alt=\"\" src=\"https://www.thegioididong.com/tin-tuc/dung-de-deal-ngon-chim-iphone-samsung-oppo-1544812\" /></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/oppo-find-x5-pro\" target=\"_blank\" title=\"Điện thoại OPPO Find X5 Pro 5G \" type=\"Điện thoại OPPO Find X5 Pro 5G \">OPPO Find X5 Pro 5G</a>&nbsp;được ho&agrave;n thiện từ vật liệu gốm cao cấp c&ugrave;ng với đ&oacute; l&agrave; những n&acirc;ng cấp về chất lượng ở camera nhờ sự hợp t&aacute;c với nh&agrave; sản xuất m&aacute;y ảnh Hasselblad. Điện thoại sở hữu khả năng quay - chụp chuẩn điện ảnh nhờ&nbsp;bộ vi xử l&yacute; h&igrave;nh ảnh NPU mang t&ecirc;n MariSilicon X được OPPO nghi&ecirc;n cứu, gi&uacute;p t&aacute;i hiện h&igrave;nh ảnh một c&aacute;ch ch&acirc;n thực v&agrave; sống động hứa hẹn sẽ l&agrave; trợ thủ đắc lực trong c&ocirc;ng việc, chụp ảnh, quay vlog, dựng video của bạn.&nbsp;</p>\r\n\r\n<p>Điện thoại sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh AMOLED ti&ecirc;n tiến, tần số qu&eacute;t cao, c&oacute; độ ph&acirc;n giải l&ecirc;n đến 2K+ 1440 x 3216 Pixels, những nội dung hiển thị tr&ecirc;n m&agrave;n h&igrave;nh được t&aacute;i hiện sắc n&eacute;t cho d&ugrave; l&agrave; chi tiết nhỏ. Hiệu năng h&agrave;ng đầu với khả năng chiến game đỉnh cao, kh&ocirc;ng bị giật lag,&nbsp;c&oacute; RAM 12 GB gi&uacute;p bạn sử dụng c&aacute;c t&aacute;c vụ đa nhiệm hết sức mượt m&agrave;. Vi&ecirc;n pin dung lượng khủng&nbsp;đến 5.000 mAh bạn thoải m&aacute;i sử dụng cả ng&agrave;y d&agrave;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/oppo-find-x5-pro\"><label>Trả góp 0%</label><img data-proid=\"250622\" src=\"https://cdn.tgdd.vn/Products/Images/42/250622/oppo-find-x5-pro-den-thumb-600x600.jpg\" title=\"OPPO Find X5 Pro 5G\" /></a></p>\r\n\r\n<p><a data-cache=\"True\" data-pro=\"3\" data-s=\"Nomal\" data-site=\"1\" href=\"https://www.thegioididong.com/dtdd/oppo-find-x5-pro\">OPPO Find X5 Pro 5G</a></p>\r\n\r\n<h6>H&Agrave;NG PHẢI CHUYỂN VỀ</h6>\r\n\r\n<p><strong>19.990.000₫</strong><br />\r\n32.990.000₫&nbsp;<i>-39%</i></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>16</p>\r\n\r\n<p>Xem đặc điểm nổi bật</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/oppo-find-x5-pro\">XEM CHI TIẾT</a></p>\r\n\r\n<h3><strong>iPhone 14 Pro Max 128GB: Giảm gi&aacute; 3.5 triệu đồng, gi&aacute; c&ograve;n 26.49 triệu đồng</strong></h3>\r\n\r\n<ul>\r\n	<li>Giảm đến 50% cho iPad khi mua k&egrave;m&nbsp;<strong>hoặc</strong>&nbsp;Thu Cũ Đổi Mới giảm đến 2 triệu.</li>\r\n	<li>Kh&ocirc;ng &aacute;p dụng trả g&oacute;p 0% Nh&agrave; T&agrave;i Ch&iacute;nh.</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"https://www.thegioididong.com/tin-tuc/dung-de-deal-ngon-chim-iphone-samsung-oppo-1544812\" /></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/iphone-14-pro-max\" target=\"_blank\" title=\"iPhone 14 Pro Max 128GB\" type=\"iPhone 14 Pro Max 128GB\">iPhone 14 Pro Max 128GB</a>&nbsp;vẫn lu&ocirc;n được săn đ&oacute;n nồng nhiệt, sở hữu camera chuy&ecirc;n nghiệp v&agrave; được trang bị vi xử l&yacute; A16 Bionic mạnh mẽ. Điện thoại được n&acirc;ng cấp cả về CPU v&agrave; GPU c&oacute; thể xử l&yacute; tốt mọi nhu cầu sử dụng của bạn. Điện thoại RAM 6 GB cho c&aacute;c thao t&aacute;c về phần đa nhiệm đều được m&aacute;y xử l&yacute; dễ d&agrave;ng, d&ugrave;ng đồng thời hoặc chuyển đổi nhiều ứng dụng kh&ocirc;ng hề xảy ra t&igrave;nh trạng khựng hay load lại từ đầu mỗi khi mở lại.</p>\r\n\r\n<p>Cụm 3 camera độc đ&aacute;o sắc n&eacute;t trong đ&oacute; camera ch&iacute;nh c&oacute; độ ph&acirc;n giải l&ecirc;n tới 48 MP c&ugrave;ng hai cảm biến phụ c&oacute; chung độ ph&acirc;n giải l&agrave; 12 MP. H&igrave;nh ảnh được thu với độ chi tiết cao, m&agrave;u sắc hiển thị rất ch&acirc;n thực r&otilde; n&eacute;t, rất th&iacute;ch hợp khi bạn l&agrave; người d&ugrave;ng điện thoại thường xuy&ecirc;n để quay chụp, phục vụ cho c&ocirc;ng việc. Ngo&agrave;i ra&nbsp;c&ograve;n nhiều chế độ quay chụp h&agrave;ng đầu chắc chắn sẽ l&agrave;m bạn ấn tượng v&agrave; h&agrave;i l&ograve;ng như: Deep Fusion, Smart HDR 4 v&agrave; Dolby Vision HDR.</p>\r\n\r\n<h3>&nbsp;</h3>\r\n'),
(12, 'Rò rỉ mới cho thấy Galaxy S24 Ultra sẽ sử dụng cảm biến 200MP được nâng cấp', 'list_post11.jpg', 'Đừng để ưu đãi ngon chìm', 'Đã đăng', '2023-10-09 17:23:03', 'vuhonglinh123', '<p><img alt=\"Samsung Galaxy S24 Ultra sẽ có camera như thế nào? (Ảnh: TechBit)\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545408/h2-010923-202734-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545408/h2-010923-202734-800-resize.jpg\" title=\"Samsung Galaxy S24 Ultra sẽ có camera như thế nào? (Ảnh: TechBit)\" /></p>\r\n\r\n<p>Samsung Galaxy S24 Ultra sẽ c&oacute; camera như thế n&agrave;o? (Ảnh: TechBit)</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s24-ultra-5g\" target=\"_blank\" title=\"Galaxy S24 Ultra\" type=\"Galaxy S24 Ultra\">Galaxy S24 Ultra</a>&nbsp;được cho l&agrave; sẽ trang bị một cảm biến ảnh mới c&oacute; độ ph&acirc;n giải 200 MP. Đ&acirc;y sẽ l&agrave; phi&ecirc;n bản tối ưu h&oacute;a của cảm biến HP2 tr&ecirc;n mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s23-ultra\" target=\"_blank\" title=\"Galaxy S23 Ultra 5G\" type=\"Galaxy S23 Ultra 5G\">Galaxy S23 Ultra 5G</a>&nbsp;năm ngo&aacute;i. Ch&uacute;ng ta c&oacute; thể mong đợi những thay đổi nhỏ nhưng đ&aacute;ng kể về chất lượng h&igrave;nh ảnh.</h2>\r\n\r\n<p><img alt=\"Bài đăng của leaker Ice Universe\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545408/hinhbai5-010923-203427-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545408/hinhbai5-010923-203427-800-resize.jpg\" title=\"Bài đăng của leaker Ice Universe\" /></p>\r\n\r\n<p>B&agrave;i đăng của leaker Ice Universe</p>\r\n\r\n<p>Leaker Ice Universe đ&atilde; đăng tải th&ocirc;ng tin mới nhất tr&ecirc;n nền tảng X. Theo đ&oacute;, flagship mới của&nbsp;<a href=\"https://www.thegioididong.com/samsung\" target=\"_blank\" title=\"Samsung\" type=\"Samsung\">Samsung</a>&nbsp;sẽ trang bị cảm biến 200 MP Samsung ISOCELL HP2SX mới.</p>\r\n\r\n<p><img alt=\"Galaxy S24 Ultra sẽ được trang bị cảm biến 200 MP Samsung ISOCELL HP2SX (Ảnh: RTL Niuews)\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545408/hinhbai3-010923-202922-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545408/hinhbai3-010923-202922-800-resize.jpg\" title=\"Galaxy S24 Ultra sẽ được trang bị cảm biến 200 MP Samsung ISOCELL HP2SX (Ảnh: RTL Niuews)\" /></p>\r\n\r\n<p>Galaxy S24 Ultra sẽ được trang bị cảm biến 200 MP Samsung ISOCELL HP2SX mới (Ảnh: RTL Niuews)</p>\r\n\r\n<p>Theo nguồn tin, cảm biến mới n&agrave;y sẽ giữ nguy&ecirc;n k&iacute;ch thước cảm biến 1/1.3-inch với k&iacute;ch thước pixel ri&ecirc;ng lẻ l&agrave; 0.6 &micro;m. Điều n&agrave;y kh&ocirc;ng tồi, khi x&eacute;t đến việc HP2 đ&atilde; được xem l&agrave; một trong những cảm biến smartphone tốt nhất. Mặc d&ugrave; cảm biến mới được đồn đo&aacute;n l&agrave; phi&ecirc;n bản tối ưu h&oacute;a của cảm biến HP2, nguồn tin chưa tiết lộ th&ecirc;m chi tiết về cảm biến n&agrave;y.</p>\r\n\r\n<p><img alt=\"Ảnh render được cho là của Samsung Galaxy S24 Ultra\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545408/hinhbai6-010923-203507-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545408/hinhbai6-010923-203507-800-resize.jpg\" title=\"Ảnh render được cho là của Samsung Galaxy S24 Ultra\" /></p>\r\n\r\n<p>Ảnh render được cho l&agrave; của Samsung Galaxy S24 Ultra</p>\r\n\r\n<p>Theo một b&aacute;o c&aacute;o gần đ&acirc;y, Samsung Galaxy S24 Ultra sẽ chỉ sử dụng vi xử l&yacute; Snapdragon 8 Gen 3. Chỉ c&oacute;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s24-plus-5g\" target=\"_blank\" title=\"Galaxy S24+\" type=\"Galaxy S24+\">Galaxy S24+</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s24-5g\" target=\"_blank\" title=\"Galaxy S24\" type=\"Galaxy S24\">Galaxy S24</a>&nbsp;dự kiến sẽ ra mắt với 2 d&ograve;ng chip Exynos 2400 v&agrave; Snapdragon 8 Gen 3 t&ugrave;y thuộc v&agrave;o khu vực. Galaxy S24 Ultra được đồn đo&aacute;n sẽ c&oacute; m&agrave;n h&igrave;nh AMOLED Quad HD+ 6.8 inch với tốc độ l&agrave;m mới 120 Hz.</p>\r\n\r\n<p>Camera ch&iacute;nh 200 MP dự kiến sẽ được kết hợp với camera si&ecirc;u rộng 12 MP, camera telephoto 50 MP hỗ trợ zoom quang 3X, v&agrave; camera periscope 10 MP hỗ trợ zoom quang 10X. Smartphone cao cấp n&agrave;y sẽ c&oacute; một camera selfie 12 MP mặt trước.</p>\r\n\r\n<p><img alt=\"Galaxy S24 Ultra sở hữu nhiều trang bị đầu bảng (Ảnh: Mobilizujeme.cz)\" data-original=\"https://cdn.tgdd.vn/Files/2023/09/01/1545408/hinhbai4-010923-203032-800-resize.jpg\" src=\"https://cdn.tgdd.vn/Files/2023/09/01/1545408/hinhbai4-010923-203032-800-resize.jpg\" title=\"Galaxy S24 Ultra sở hữu nhiều trang bị đầu bảng (Ảnh: Mobilizujeme.cz)\" /></p>\r\n\r\n<p>Galaxy S24 Ultra sở hữu nhiều trang bị đầu bảng (Ảnh: Mobilizujeme.cz)</p>\r\n\r\n<p>Thiết bị dự kiến ​​sẽ được trang bị vi&ecirc;n pin 5.000 mAh hỗ trợ sạc nhanh 45 W. Ch&uacute;ng ta c&oacute; thể mong đợi sản phẩm sẽ chạy phi&ecirc;n bản One UI dựa tr&ecirc;n Android 14 mới nhất.</p>\r\n');
